namespace knn_gpu {

using idx_t  = int32_t;
using dist_t = float;

struct DeviceVectors {
    float* data;      // row-major [n_rows x dim]
    idx_t  n_rows;
    int    dim;
};

struct KnnGraph {
    DeviceVectors  vectors;          // исходные векторы
    idx_t*         neighbors;        // [n_rows x k] индексы соседей
    dist_t*        distances;        // [n_rows x k] расстояния
    idx_t          k;
};

/* ========== высокоуровневые операции ========== */
KnnGraph build_naive_knn_graph(const float* h_data,
                                idx_t n_rows,
                                int dim,
                                idx_t k,
                                int device = 0);

void refine_graph(KnnGraph& graph, int hops = 2);

void save_graph(const KnnGraph& g, const std::string& path);
KnnGraph load_graph(const std::string& path, int device = 0);

/* обрезать граф до первых new_rows строк */
void trim_graph(KnnGraph& g, idx_t new_rows);

/* batch-запросы */
void search_on_graph(const KnnGraph& g,
                     const float* h_queries,
                     idx_t n_queries,
                     idx_t k_query,
                     idx_t* h_out_idx,
                     dist_t* h_out_dist,
                     int beam_size = 32,
                     bool verbose  = false);

/* освободить всё GPU */
void free_graph(KnnGraph& g);

