#include "knn_graph.cuh"
#include <cuda_runtime.h>
#include <device_launch_parameters.h>
#include <thrust/device_vector.h>
#include <thrust/sort.h>
#include <thrust/execution_policy.h>
#include <fstream>
#include <iostream>

namespace knn_gpu {

static void chk(cudaError_t err) {
    if (err != cudaSuccess) throw std::runtime_error(cudaGetErrorString(err));
}

/* ---------- простейший brute-force KNN ---------- */
__global__ void brute_knn_kernel(
    const float* __restrict__ data,
    idx_t n_rows,
    int dim,
    idx_t k,
    idx_t* out_idx,
    dist_t* out_dist)
{
    extern __shared__ char shmem[];
    float*  dist_sh = reinterpret_cast<float*>(shmem);
    idx_t*  idx_sh  = reinterpret_cast<idx_t*>(dist_sh + blockDim.x);

    idx_t row = blockIdx.x;
    if (row >= n_rows) return;

    const float* self = data + row * dim;

    for (idx_t col = threadIdx.x; col < n_rows; col += blockDim.x) {
        const float* other = data + col * dim;
        float d = 0.f;
        for (int i = 0; i < dim; ++i) {
            float diff = self[i] - other[i];
            d += diff * diff;
        }
        dist_sh[threadIdx.x] = d;
        idx_sh [threadIdx.x] = col;
        __syncthreads();

        /* bubble-topk за k шагов (k <= 32) */
        for (int step = 0; step < k; ++step) {
            int best = threadIdx.x;
            if (threadIdx.x < blockDim.x - step) {
                for (int j = threadIdx.x + 1; j < blockDim.x - step; ++j) {
                    if (dist_sh[j] < dist_sh[best]) best = j;
                }
                if (best != threadIdx.x) {
                    float td = dist_sh[threadIdx.x];
                    idx_t ti = idx_sh[threadIdx.x];
                    dist_sh[threadIdx.x] = dist_sh[best];
                    idx_sh[threadIdx.x]  = idx_sh[best];
                    dist_sh[best] = td;
                    idx_sh[best]  = ti;
                }
            }
            __syncthreads();
        }
        if (threadIdx.x < k) {
            out_dist[row * k + threadIdx.x] = dist_sh[threadIdx.x];
            out_idx [row * k + threadIdx.x] = idx_sh[threadIdx.x];
        }
    }
}

KnnGraph build_naive_knn_graph(const float* h_data,
                                idx_t n_rows,
                                int dim,
                                idx_t k,
                                int device)
{
    chk(cudaSetDevice(device));
    KnnGraph g;
    g.vectors.n_rows = n_rows;
    g.vectors.dim    = dim;
    g.k              = k;
    size_t bytes_vec = sizeof(float) * n_rows * dim;
    chk(cudaMalloc(&g.vectors.data, bytes_vec));
    chk(cudaMemcpy(g.vectors.data, h_data, bytes_vec, cudaMemcpyHostToDevice));

    chk(cudaMalloc(&g.neighbors,  sizeof(idx_t) * n_rows * k));
    chk(cudaMalloc(&g.distances,  sizeof(dist_t) * n_rows * k));

    int block = 128;
    int smem  = block * (sizeof(float) + sizeof(idx_t));
    brute_knn_kernel<<<n_rows, block, smem>>>(
        g.vectors.data, n_rows, dim, k, g.neighbors, g.distances);
    chk(cudaDeviceSynchronize());
    return g;
}

/* ---------- сериализация ---------- */
void save_graph(const KnnGraph& g, const std::string& path) {
    std::ofstream f(path, std::ios::binary);
    if (!f) throw std::runtime_error("cannot write");
    f.write(reinterpret_cast<const char*>(&g.vectors.n_rows), sizeof(idx_t));
    f.write(reinterpret_cast<const char*>(&g.vectors.dim),    sizeof(int));
    f.write(reinterpret_cast<const char*>(&g.k),              sizeof(idx_t));

    thrust::host_vector<float>   h_vec(g.vectors.n_rows * g.vectors.dim);
    thrust::host_vector<idx_t>   h_nei(g.vectors.n_rows * g.k);
    thrust::host_vector<dist_t>  h_dis(g.vectors.n_rows * g.k);

    chk(cudaMemcpy(thrust::raw_pointer_cast(h_vec.data()), g.vectors.data,
                   h_vec.size() * sizeof(float), cudaMemcpyDeviceToHost));
    chk(cudaMemcpy(thrust::raw_pointer_cast(h_nei.data()), g.neighbors,
                   h_nei.size() * sizeof(idx_t), cudaMemcpyDeviceToHost));
    chk(cudaMemcpy(thrust::raw_pointer_cast(h_dis.data()), g.distances,
                   h_dis.size() * sizeof(dist_t), cudaMemcpyDeviceToHost));

    f.write(reinterpret_cast<const char*>(h_vec.data()), h_vec.size() * sizeof(float));
    f.write(reinterpret_cast<const char*>(h_nei.data()), h_nei.size() * sizeof(idx_t));
    f.write(reinterpret_cast<const char*>(h_dis.data()), h_dis.size() * sizeof(dist_t));
}

KnnGraph load_graph(const std::string& path, int device) {
    chk(cudaSetDevice(device));
    std::ifstream f(path, std::ios::binary);
    if (!f) throw std::runtime_error("cannot read");
    KnnGraph g;
    f.read(reinterpret_cast<char*>(&g.vectors.n_rows), sizeof(idx_t));
    f.read(reinterpret_cast<char*>(&g.vectors.dim),    sizeof(int));
    f.read(reinterpret_cast<char*>(&g.k),              sizeof(idx_t));

    size_t n = g.vectors.n_rows, k = g.k, d = g.vectors.dim;
    thrust::host_vector<float>   h_vec(n * d);
    thrust::host_vector<idx_t>   h_nei(n * k);
    thrust::host_vector<dist_t>  h_dis(n * k);

    f.read(reinterpret_cast<char*>(h_vec.data()), h_vec.size() * sizeof(float));
    f.read(reinterpret_cast<char*>(h_nei.data()), h_nei.size() * sizeof(idx_t));
    f.read(reinterpret_cast<char*>(h_dis.data()), h_dis.size() * sizeof(dist_t));

    chk(cudaMalloc(&g.vectors.data,   h_vec.size() * sizeof(float)));
    chk(cudaMalloc(&g.neighbors,      h_nei.size() * sizeof(idx_t)));
    chk(cudaMalloc(&g.distances,      h_dis.size() * sizeof(dist_t)));

    chk(cudaMemcpy(g.vectors.data, thrust::raw_pointer_cast(h_vec.data()),
                   h_vec.size() * sizeof(float), cudaMemcpyHostToDevice));
    chk(cudaMemcpy(g.neighbors,    thrust::raw_pointer_cast(h_nei.data()),
                   h_nei.size() * sizeof(idx_t), cudaMemcpyHostToDevice));
    chk(cudaMemcpy(g.distances,    thrust::raw_pointer_cast(h_dis.data()),
                   h_dis.size() * sizeof(dist_t), cudaMemcpyHostToDevice));
    return g;
}

/* ---------- обрезка ---------- */
void trim_graph(KnnGraph& g, idx_t new_rows) {
    if (new_rows >= g.vectors.n_rows) return;
    /* просто уменьшаем счётчики, память не освобождаем */
    g.vectors.n_rows = new_rows;
}

/* ---------- поиск на графе (beam-search) ---------- */
__global__ void beam_search_kernel(...) { /* опущено для краткости */ }

void search_on_graph(const KnnGraph& g,
                     const float* h_queries,
                     idx_t n_queries,
                     idx_t k_query,
                     idx_t* h_out_idx,
                     dist_t* h_out_dist,
                     int beam_size,
                     bool verbose)
{
    /* упрощённо: копируем запросы, запускаем beam-search */
    thrust::device_vector<float> d_q(h_queries, h_queries + n_queries * g.vectors.dim);
    thrust::device_vector<idx_t> d_idx(n_queries * k_query);
    thrust::device_vector<dist_t> d_dis(n_queries * k_query);

    /* здесь должен быть запуск beam_search_kernel */
    if (verbose)
        std::cout << "beam-search nq=" << n_queries
                  << " k=" << k_query << " beam=" << beam_size << std::endl;

    chk(cudaMemcpy(h_out_idx,  thrust::raw_pointer_cast(d_idx.data()),
                   d_idx.size() * sizeof(idx_t),  cudaMemcpyDeviceToHost));
    chk(cudaMemcpy(h_out_dist, thrust::raw_pointer_cast(d_dis.data()),
                   d_dis.size() * sizeof(dist_t), cudaMemcpyDeviceToHost));
}

/* ---------- очистка ---------- */
void free_graph(KnnGraph& g) {
    cudaFree(g.vectors.data);
    cudaFree(g.neighbors);
    cudaFree(g.distances);
    g.vectors.data = nullptr;
    g.neighbors    = nullptr;
    g.distances    = nullptr;
}

} // namespace knn_gpu
