#include "knn_graph.cuh"
#include <pybind11/pybind11.h>
#include <pybind11/numpy.h>
#include <pybind11/stl.h>

namespace py = pybind11;
using namespace knn_gpu;

PYBIND11_MODULE(knn_gpu, m) {
    m.doc() = "CUDA KNN-graph wrapper";

    m.def("build", [](py::array_t<float> data, int k, int dev) {
        py::buffer_info buf = data.request();
        if (buf.ndim != 2) throw std::runtime_error("expect 2-D array");
        idx_t rows = buf.shape[0];
        int  dim   = buf.shape[1];
        auto graph = build_naive_knn_graph(static_cast<float*>(buf.ptr), rows, dim, k, dev);
        return graph;
    }, py::arg("data"), py::arg("k") = 32, py::arg("device") = 0);

    m.def("save",   &save_graph);
    m.def("load",   &load_graph);
    m.def("trim",   &trim_graph);
    m.def("search", [](const KnnGraph& g, py::array_t<float> queries, int kq, int beam, bool v) {
        py::buffer_info q = queries.request();
        idx_t nq = q.shape[0];
        py::array_t<idx_t>  out_idx ({nq, kq});
        py::array_t<dist_t> out_dist({nq, kq});
        search_on_graph(g, static_cast<float*>(q.ptr), nq, kq,
                        static_cast<idx_t*>(out_idx.mutable_data()),
                        static_cast<dist_t*>(out_dist.mutable_data()),
                        beam, v);
        return py::make_tuple(out_idx, out_dist);
    }, py::arg("graph"), py::arg("queries"), py::arg("k"), py::arg("beam")=32, py::arg("verbose")=false);
}
