#include "knn_graph.cuh"
#include <iostream>
#include <vector>
#include <chrono>

int main(int argc, char* argv[]) {
    if (argc < 3) {
        std::cerr << "Usage: " << argv[0] << " file.fvecs K [--save path] [--query q.fvecs]\n";
        return 1;
    }
    std::string data_file = argv[1];
    int k = std::stoi(argv[2]);

    /* читаем fvecs (простой бинарник: 4 bytes dim, 4*dim bytes vector) */
    FILE* f = fopen(data_file.c_str(), "rb");
    if (!f) { std::cerr << "no file\n"; return 1; }
    int dim;
    fread(&dim, 4, 1, f);
    fseek(f, 0, SEEK_END);
    size_t n_rows = ftell(f) / (4 + 4 * dim);
    fseek(f, 0, SEEK_SET);
    std::vector<float> h_data(n_rows * dim);
    for (size_t i = 0; i < n_rows; ++i) {
        int d; fread(&d, 4, 1, f);
        fread(h_data.data() + i * dim, 4, dim, f);
    }
    fclose(f);

    auto t0 = std::chrono::high_resolution_clock::now();
    auto g = knn_gpu::build_naive_knn_graph(h_data.data(), n_rows, dim, k);
    auto t1 = std::chrono::high_resolution_clock::now();
    std::cout << "built in "
              << std::chrono::duration<double>(t1 - t0).count()
              << " s\n";

    for (int i = 2; i < argc; ++i) {
        if (std::string(argv[i]) == "--save" && i + 1 < argc) {
            knn_gpu::save_graph(g, argv[++i]);
            std::cout << "saved\n";
        }
        if (std::string(argv[i]) == "--query" && i + 1 < argc) {
            std::string qfile = argv[++i];
            FILE* qf = fopen(qfile.c_str(), "rb");
            int qdim; fread(&qdim, 4, 1, qf);
            if (qdim != dim) { std::cerr << "dim mismatch\n"; return 1; }
            fseek(qf, 0, SEEK_END);
            size_t nq = ftell(qf) / (4 + 4 * dim);
            fseek(qf, 0, SEEK_SET);
            std::vector<float> h_queries(nq * dim);
            for (size_t j = 0; j < nq; ++j) {
                int d; fread(&d, 4, 1, qf);
                fread(h_queries.data() + j * dim, 4, dim, qf);
            }
            fclose(qf);
            std::vector<knn_gpu::idx_t>  out_idx (nq * k);
            std::vector<knn_gpu::dist_t> out_dist(nq * k);
            knn_gpu::search_on_graph(g, h_queries.data(), nq, k,
                                     out_idx.data(), out_dist.data(), 32, true);
        }
    }
    knn_gpu::free_graph(g);
    return 0;
}
