// AddHeader добавляет произвольный заголовок, который будет
// отправляться во всех запросах.
func (b *TransportBuilder) AddHeader(key, value string) *TransportBuilder {
	b.headers[key] = value
	return b
}

// SetRequestTimeout задаёт глобальный тайм-аут запроса.
func (b *TransportBuilder) SetRequestTimeout(d time.Duration) *TransportBuilder {
	b.requestTimeout = d
	return b
}

// Build строит и возвращает готовый elasticsearch.Client.
func (b *TransportBuilder) Build() (*elasticsearch.Client, error) {
	if len(b.addresses) == 0 {
		return nil, errors.New("addresses required")
	}

	// 1. Настраиваем собственный http.Transport
	tr := &http.Transport{
		TLSClientConfig: &tls.Config{
			InsecureSkipVerify: b.insecureSkipVerify,
		},
	}

	// 1a. Прокси
	if b.noProxy {
		tr.Proxy = nil
	} else if b.proxyURL != "" {
		u, err := url.Parse(b.proxyURL)
		if err != nil {
			return nil, fmt.Errorf("invalid proxy url: %w", err)
		}
		if b.proxyUser != "" {
			u.User = url.UserPassword(b.proxyUser, b.proxyPassword)
		}
		tr.Proxy = http.ProxyURL(u)
	}

	// 1b. CA-сертификат
	if len(b.caCert) > 0 {
		pool := x509.NewCertPool()
		if !pool.AppendCertsFromPEM(b.caCert) {
			return nil, errors.New("failed to parse CA certificate")
		}
		tr.TLSClientConfig.RootCAs = pool
	}

	// 2. Оборачиваем Transport в наш roundTripper, чтобы добавить
	//    фиксированные заголовки и тайм-аут.
	rt := &roundTripper{
		rt:      tr,
		headers: b.headers,
		timeout: b.requestTimeout,
	}

	// 3. Формируем конфигурацию клиента
	cfg := elasticsearch.Config{
		Addresses: b.addresses,
		Transport: rt,
	}

	// 3a. Аутентификация
	if b.apiKey != "" {
		cfg.APIKey = b.apiKey
	} else if b.username != "" {
		cfg.Username = b.username
		cfg.Password = b.password
	}

	return elasticsearch.NewClient(cfg)
}

// roundTripper добавляет заголовки и тайм-аут к каждому запросу.
type roundTripper struct {
	rt      http.RoundTripper
	headers map[string]string
	timeout time.Duration
}

func (r *roundTripper) RoundTrip(req *http.Request) (*http.Response, error) {
	if r.timeout > 0 {
		ctx, cancel := context.WithTimeout(req.Context(), r.timeout)
		defer cancel()
		req = req.WithContext(ctx)
	}
	for k, v := range r.headers {
		req.Header.Set(k, v)
	}
	return r.rt.RoundTrip(req)
}
```

