```js
#!/usr/bin/env node
/* eslint-disable no-console */
import fs from 'fs-extra';
import path from 'node:path';
import { fileURLToPath } from 'node:url';
import peg from 'pegjs';
import pug from 'pug';
import chokidar from 'chokidar';
import browserSync from 'browser-sync';
import pc from 'picocolors';

const __dirname = path.dirname(fileURLToPath(import.meta.url));
const ROOT = path.resolve(__dirname);
const SRC = path.join(ROOT, 'src');
const OUT = path.join(ROOT, 'public');

const bs = browserSync.create();

/* ---------- конфигурация ---------- */
const tasks = {
  parsers: { src: 'parsers/*.pegjs', out: 'js/parsers' },
  templates: { src: 'templates/*.pug', out: '.' },
  assets: { src: 'assets/**/*', out: '.' }
};

const publicSkeleton = {
  folders: ['css', 'js', 'img', 'fonts'],
  files: [
    { name: 'css/style.css', content: '/* placeholder */' },
    { name: 'js/app.js', content: 'console.log("hello");' }
  ]
};

/* ---------- утилиты ---------- */
const log = (msg, color = 'green') => console.log(pc[color](`[build] ${msg}`));
const ensureDir = (p) => fs.ensureDirSync(path.dirname(p));

/* ---------- шаги ---------- */
async function buildParsers() {
  const cwd = path.join(SRC, 'parsers');
  const files = await fs.readdir(cwd).catch(() => []);
  await Promise.all(
    files
      .filter((f) => f.endsWith('.pegjs'))
      .map(async (f) => {
        const grammar = await fs.readFile(path.join(cwd, f), 'utf8');
        const parser = peg.generate(grammar, { output: 'source', format: 'commonjs' });
        const outFile = path.join(OUT, 'js/parsers', f.replace(/\.pegjs$/, '.js'));
        ensureDir(outFile);
        await fs.writeFile(outFile, parser);
        log(`built parser ${f}`, 'cyan');
      })
  );
}

async function buildTemplates() {
  const cwd = path.join(SRC, 'templates');
  const files = await fs.readdir(cwd).catch(() => []);
  await Promise.all(
    files
      .filter((f) => f.endsWith('.pug'))
      .map(async (f) => {
        const fn = path.join(cwd, f);
        const html = pug.renderFile(fn, { pretty: true });
        const outFile = path.join(OUT, f.replace(/\.pug$/, '.html'));
        ensureDir(outFile);
        await fs.writeFile(outFile, html);
        log(`built template ${f}`, 'cyan');
      })
  );
}

async function copyAssets() {
  const src = path.join(SRC, 'assets');
  const dest = OUT;
  await fs.copy(src, dest, { overwrite: true });
  log('assets copied', 'cyan');
}

async function createPublicSkeleton() {
  await Promise.all(
    publicSkeleton.folders.map((d) => fs.ensureDir(path.join(OUT, d)))
  );
  await Promise.all(
    publicSkeleton.files.map(({ name, content }) =>
      fs.writeFile(path.join(OUT, name), content)
    )
  );
  log('public skeleton created', 'blue');
}

/* ---------- главная функция ---------- */
async function build() {
  log('start build');
  await createPublicSkeleton();
  await Promise.all([buildParsers(), buildTemplates(), copyAssets()]);
  log('build complete', 'green');
}

/* ---------- watch + serve ---------- */
function watch() {
  const watcher = chokidar.watch(
    [path.join(SRC, '**/*')],
    { ignored: /(^|[\/\\])\../, persistent: true }
  );
  watcher.on('change', async (p) => {
    log(`file changed: ${path.relative(ROOT, p)}`, 'yellow');
    try {
      if (p.endsWith('.pegjs')) await buildParsers();
      else if (p.endsWith('.pug')) await buildTemplates();
      else await copyAssets();
      bs.reload();
    } catch (e) {
      console.error(e);
    }
  });
}

function serve() {
  bs.init({ server: OUT, port: 3000, open: false });
  log('dev server on http://localhost:3000', 'magenta');
}

/* ---------- CLI ---------- */
const flags = process.argv.slice(2);
(async () => {
  await build();
  if (flags.includes('--watch') || flags.includes('--serve')) {
    watch();
    serve();
  }
})();
```

