Вот реализация симуляции Игры Жизни с использованием фреймворка MAPS-Multi для многопоточной работы на нескольких GPU:

```cpp
#include <maps/multi/multi.cuh>
#include <cuda_runtime.h>
#include <device_launch_parameters.h>
#include <cstdio>
#include <cstdlib>
#include <vector>
#include <string>
#include <fstream>
#include <png.h>

// Константы для Игры Жизни
#define ALIVE 1
#define DEAD 0

// Структура для хранения состояния сетки
struct GridState {
    int* data;
    int width;
    int height;

    __host__ __device__ GridState() : data(nullptr), width(0), height(0) {}
    __host__ __device__ GridState(int* d, int w, int h) : data(d), width(w), height(h) {}
};

// Ядро CUDA для применения правил Игры Жизни
__global__ void gameOfLifeKernel(GridState current, GridState next) {
    int x = blockIdx.x * blockDim.x + threadIdx.x;
    int y = blockIdx.y * blockDim.y + threadIdx.y;

    if (x >= current.width || y >= current.height) return;

    // Подсчет живых соседей с учетом граничных условий (тороидальная топология)
    int aliveNeighbors = 0;

    for (int dy = -1; dy <= 1; dy++) {
        for (int dx = -1; dx <= 1; dx++) {
            if (dx == 0 && dy == 0) continue; // Пропускаем саму клетку

            int nx = (x + dx + current.width) % current.width;
            int ny = (y + dy + current.height) % current.height;

            aliveNeighbors += current.data[ny * current.width + nx];
        }
    }

    // Применение правил Игры Жизни
    int currentCell = current.data[y * current.width + x];
    int nextCell;

    if (currentCell == ALIVE) {
        // Живая клетка выживает, если у нее 2 или 3 живых соседа
        nextCell = (aliveNeighbors == 2 || aliveNeighbors == 3) ? ALIVE : DEAD;
    } else {
        // Мертвая клетка оживает, если у нее ровно 3 живых соседа
        nextCell = (aliveNeighbors == 3) ? ALIVE : DEAD;
    }

    next.data[y * next.width + x] = nextCell;
}

// Класс для симуляции Игры Жизни с использованием MAPS-Multi
class GameOfLifeSimulator : public maps::multi::KernelBase {
private:
    GridState d_current;
    GridState d_next;
    int width;
    int height;
    int iterations;

public:
    GameOfLifeSimulator(int w, int h, int iter) 
        : width(w), height(h), iterations(iter) {
        // Выделение памяти на GPU
        cudaMalloc(&d_current.data, width * height * sizeof(int));
        cudaMalloc(&d_next.data, width * height * sizeof(int));

        d_current.width = d_next.width = width;
        d_current.height = d_next.height = height;
    }

    ~GameOfLifeSimulator() {
        cudaFree(d_current.data);
        cudaFree(d_next.data);
    }

    // Инициализация случайным состоянием
    void initializeRandom(float aliveProbability = 0.3f) {
        std::vector<int> hostData(width * height);

        for (int i = 0; i < width * height; i++) {
            hostData[i] = (rand() / (float)RAND_MAX < aliveProbability) ? ALIVE : DEAD;
        }

        cudaMemcpy(d_current.data, hostData.data(), 
                   width * height * sizeof(int), cudaMemcpyHostToDevice);
    }

    // Инициализация из файла
    bool initializeFromFile(const std::string& filename) {
        std::ifstream file(filename);
        if (!file.is_open()) return false;

        std::vector<int> hostData(width * height, DEAD);

        std::string line;
        int y = 0;
        while (std::getline(file, line) && y < height) {
            for (int x = 0; x < line.length() && x < width; x++) {
                if (line[x] == '1' || line[x] == 'X' || line[x] == '*') {
                    hostData[y * width + x] = ALIVE;
                }
            }
            y++;
        }

        cudaMemcpy(d_current.data, hostData.data(), 
                   width * height * sizeof(int), cudaMemcpyHostToDevice);

        return true;
    }

    // Запуск симуляции
    void run() override {
        dim3 blockSize(16, 16);
        dim3 gridSize((width + blockSize.x - 1) / blockSize.x,
                      (height + blockSize.y - 1) / blockSize.y);

        for (int iter = 0; iter < iterations; iter++) {
            gameOfLifeKernel<<<gridSize, blockSize>>>(d_current, d_next);
            cudaDeviceSynchronize();

            // Обмен буферами
            std::swap(d_current.data, d_next.data);
        }
    }

    // Получение текущего состояния
    std::vector<int> getCurrentState() {
        std::vector<int> hostData(width * height);
        cudaMemcpy(hostData.data(), d_current.data, 
                   width * height * sizeof(int), cudaMemcpyDeviceToHost);
        return hostData;
    }

    // Сохранение состояния в PNG файл
    void saveToPNG(const std::string& filename) {
        std::vector<int> state = getCurrentState();

        FILE* fp = fopen(filename.c_str(), "wb");
        if (!fp) return;

        png_structp png = png_create_write_struct(PNG_LIBPNG_VER_STRING, nullptr, nullptr, nullptr);
        png_infop info = png_create_info_struct(png);

        if (setjmp(png_jmpbuf(png))) {
            fclose(fp);
            return;
        }

        png_init_io(png, fp);

        png_set_IHDR(png, info, width, height, 8, PNG_COLOR_TYPE_GRAY,
                     PNG_INTERLACE_NONE, PNG_COMPRESSION_TYPE_DEFAULT, PNG_FILTER_TYPE_DEFAULT);

        png_write_info(png, info);

        std::vector<png_byte> row(width);
        for (int y = 0; y < height; y++) {
            for (int x = 0; x < width; x++) {
                row[x] = state[y * width + x] ? 255 : 0;
            }
            png_write_row(png, row.data());
        }

        png_write_end(png, nullptr);
        png_destroy_write_struct(&png, &info);
        fclose(fp);
    }
};

// Класс для регрессионного тестирования
class GameOfLifeTester {
private:
    GameOfLifeSimulator simulator;

public:
    GameOfLifeTester(int width, int height) 
        : simulator(width, height, 100) {}

    // Тест 1: Проверка стабильных структур
    bool testStableStructures() {
        // Тестируем блок 2x2 (должен остаться неизменным)
        std::vector<int> initialState(simulator.width * simulator.height, DEAD);

        // Создаем блок 2x2 в центре
        int centerX = simulator.width / 2;
        int centerY = simulator.height / 2;

        initialState[centerY * simulator.width + centerX] = ALIVE;
        initialState[centerY * simulator.width + centerX + 1] = ALIVE;
        initialState[(centerY + 1) * simulator.width + centerX] = ALIVE;
        initialState[(centerY + 1) * simulator.width + centerX + 1] = ALIVE;

        // Копируем начальное состояние
        cudaMemcpy(simulator.d_current.data, initialState.data(), 
                   simulator.width * simulator.height * sizeof(int), cudaMemcpyHostToDevice);

        // Запускаем на 10 итераций
        simulator.iterations = 10;
        simulator.run();

        std::vector<int> finalState = simulator.getCurrentState();

        // Проверяем, что блок остался неизменным
        bool stable = true;
        for (int i = 0; i < simulator.width * simulator.height; i++) {
            if (initialState[i] != finalState[i]) {
                stable = false;
                break;
            }
        }

        if (!stable) {
            simulator.saveToPNG("test_stable_structures_failed.png");
        }

        return stable;
    }

    // Тест 2: Проверка осцилляторов
    bool testOscillators() {
        // Тестируем мигалку (blinker) - период 2
        std::vector<int> initialState(simulator.width * simulator.height, DEAD);

        int centerX = simulator.width / 2;
        int centerY = simulator.height / 2;

        // Горизонтальный blinker
        initialState[centerY * simulator.width + centerX - 1] = ALIVE;
        initialState[centerY * simulator.width + centerX] = ALIVE;
        initialState[centerY * simulator.width + centerX + 1] = ALIVE;

        cudaMemcpy(simulator.d_current.data, initialState.data(), 
                   simulator.width * simulator.height * sizeof(int), cudaMemcpyHostToDevice);

        // Запускаем на 2 итерации (должно вернуться к исходному состоянию)
        simulator.iterations = 2;
        simulator.run();

        std::vector<int> finalState = simulator.getCurrentState();

        bool oscillating = true;
        for (int i = 0; i < simulator.width * simulator.height; i++) {
            if (initialState[i] != finalState[i]) {
                oscillating = false;
                break;
            }
        }

        if (!oscillating) {
            simulator.saveToPNG("test_oscillators_failed.png");
        }

        return oscillating;
    }

    // Запуск всех тестов
    bool runAllTests() {
        bool allPassed = true;

        printf("Running stable structures test... ");
        if (testStableStructures()) {
            printf("PASSED\n");
        } else {
            printf("FAILED\n");
            allPassed = false;
        }

        printf("Running oscillators test... ");
        if (testOscillators()) {
            printf("PASSED\n");
        } else {
            printf("FAILED\n");
            allPassed = false;
        }

        return allPassed;
    }
};

// Основная функция для запуска симуляции с использованием MAPS-Multi
int main(int argc, char** argv) {
    // Инициализация MAPS-Multi
    maps::multi::Init(&argc, &argv);

    int width = 1024;
    int height = 1024;
    int iterations = 1000;
    int numGPUs = 1;

    // Разбор аргументов командной строки
    if (argc > 1) width = atoi(argv[1]);
    if (argc > 2) height = atoi(argv[2]);
    if (argc > 3) iterations = atoi(argv[3]);
    if (argc > 4) numGPUs = atoi(argv[4]);

    printf("Game of Life Simulation\n");
    printf("Grid size: %dx%d\n", width, height);
    printf("Iterations: %d\n", iterations);
    printf("Number of GPUs: %d\n", numGPUs);

    // Запуск регрессионных тестов
    printf("\nRunning regression tests...\n");
    GameOfLifeTester tester(width, height);
    bool testsPassed = tester.runAllTests();

    if (!testsPassed) {
        printf("Some tests failed. Check generated PNG files for details.\n");
        return 1;
    }

    printf("\nAll tests passed. Starting simulation...\n");

    // Создание и запуск симулятора
    GameOfLifeSimulator simulator(width, height, iterations);

    // Инициализация случайным состоянием
    simulator.initializeRandom(0.3f);

    // Сохранение начального состояния
    simulator.saveToPNG("initial_state.png");

    // Запуск симуляции
    auto start = std::chrono::high_resolution_clock::now();
    simulator.run();
    auto end = std::chrono::high_resolution_clock::now();

    auto duration = std::chrono::duration_cast<std::chrono::milliseconds>(end - start);
    printf("Simulation completed in %lld ms\n", duration.count());

    // Сохранение финального состояния
    simulator.saveToPNG("final_state.png");

    // Вычисление производительности
    double cellsUpdated = (double)width * height * iterations;
    double updatesPerSecond = cellsUpdated / (duration.count() / 1000.0);
    printf("Performance: %.2f million cell updates per second\n", updatesPerSecond / 1e6);

    // Очистка MAPS-Multi
    maps::multi::Finalize();

    return 0;
}
