            }
        }
    }

    // Сортируем расстояния для каждого узла
    for (int i = 0; i < n; ++i) {
        thrust::sort_by_key(distances.begin() + i * n, distances.begin() + (i + 1) * n, CompareDistance());
        // Копируем k ближайших соседей
        for (int j = 0; j < k; ++j) {
            graph.neighbors[i * k + j] = distances[i * n + j].second;
            graph.distances[i * k + j] = distances[i * n + j].first;
