// TransportBuilder is a builder for configuring the HTTP transport.
type TransportBuilder struct {
	proxyURL     string
	proxyUser    string
	proxyPass    string
	disableProxy bool
	username     string
	password     string
	certFile     string
	headers      http.Header
	timeout      time.Duration
}

// Build creates a new HTTP transport with the configured options.
func (b *TransportBuilder) Build() (*eshttp.Transport, error) {
	// Configure proxy
	var proxyTransport *proxy.Transport
	if !b.disableProxy && b.proxyURL != "" {
		proxyAuth := ""
		if b.proxyUser != "" && b.proxyPass != "" {
			proxyAuth = b.proxyUser + ":" + b.proxyPass
		}
		var err error
		proxyTransport, err = proxy.New(b.proxyURL, proxyAuth)
		if err != nil {
			return nil, err
		}
	}

	// Configure TLS
	tlsConfig, err := tls.New(b.certFile)
	if err != nil {
		return nil, err
	}

	// Create base transport
	baseTransport := &http.Transport{
		TLSClientConfig: tlsConfig,
		Proxy:           proxyTransport,
	}

	// Apply headers
	if b.headers != nil {
		for k, values := range b.headers {
			for _, v := range values {
				baseTransport.Proxy = func(req *http.Request) (*url.URL, error) {
					req.Header.Set(k, v)
					return proxyTransport.Proxy(req)
				}
			}
		}
	}

	// Create client with timeout
	client := &http.Client{
		Transport: baseTransport,
		Timeout:   b.timeout,
	}

	// Create Elasticsearch transport
	esTransport := eshttp.New(client)

	return esTransport, nil
}
