    // Для упрощения, просто выводим в консоль
    printf("Saving image to %s\n", filename);
}

// Пример использования
int main() {
    int width = 1024;
    int height = 1024;

    // Выделение памяти на GPU
    int* currentGrid;
    int* nextGrid;
    cudaMalloc(&currentGrid, width * height * sizeof(int));
    cudaMalloc(&nextGrid, width * height * sizeof(int));

    // Инициализация текущей сетки (пример: случайные значения)
    // Здесь можно загрузить данные из файла или использовать тестовые данные

    // Запуск ядра
    LifeKernelParams params = {width, height, currentGrid, nextGrid};
    runLifeKernel(&params);

    // Копирование результата на CPU
    int* hostGrid = (int*)malloc(width * height * sizeof(int));
    copyGridToHost(hostGrid, nextGrid, width * height);

    // Проверка результата (пример: сохранение изображения)
    saveImage(hostGrid, width, height, "output.png");

