#include <cuda_runtime.h>
#include <device_launch_parameters.h>
#include <stdio.h>

// Структура для хранения параметров ядра
struct LifeKernelParams {
    int width;
    int height;
    int* currentGrid;
    int* nextGrid;
};

// Функция ядра CUDA
__global__ void lifeKernel(int* currentGrid, int* nextGrid, int width, int height) {
    int x = blockIdx.x * blockDim.x + threadIdx.x;
    int y = blockIdx.y * blockDim.y + threadIdx.y;

    if (x >= width || y >= height) return;

    int pos = y * width + x;
    int liveNeighbors = 0;

    // Проверка всех 8 соседей
    for (int dy = -1; dy <= 1; dy++) {
        for (int dx = -1; dx <= 1; dx++) {
            if (dx == 0 && dy == 0) continue; // Пропускаем текущую клетку
            int nx = x + dx;
            int ny = y + dy;
            if (nx >= 0 && nx < width && ny >= 0 && ny < height) {
                int neighborPos = ny * width + nx;
                liveNeighbors += currentGrid[neighborPos];
            }
        }
    }

    // Применение правил Игры Жизни
    if (currentGrid[pos] == 1) {
        if (liveNeighbors < 2 || liveNeighbors > 3) {
            nextGrid[pos] = 0;
        } else {
            nextGrid[pos] = 1;
        }
    } else {
        if (liveNeighbors == 3) {
            nextGrid[pos] = 1;
        } else {
            nextGrid[pos] = 0;
        }
    }
}

// Вспомогательная функция для запуска ядра
void launchLifeKernel(int* currentGrid, int* nextGrid, int width, int height, int blockSize) {
    dim3 blockDim(blockSize, blockSize);
    dim3 gridDim((width + blockDim.x - 1) / blockDim.x, (height + blockDim.y - 1) / blockDim.y);

    lifeKernel<<<gridDim, blockDim>>>(currentGrid, nextGrid, width, height);
    cudaDeviceSynchronize(); // Ожидание завершения ядра
}

// Функция для копирования данных с GPU на CPU
void copyGridToHost(int* hostGrid, int* deviceGrid, int size) {
    cudaMemcpy(hostGrid, deviceGrid, size * sizeof(int), cudaMemcpyDeviceToHost);
}

// Функция для копирования данных с CPU на GPU
void copyGridToDevice(int* deviceGrid, int* hostGrid, int size) {
    cudaMemcpy(deviceGrid, hostGrid, size * sizeof(int), cudaMemcpyHostToDevice);
}

// Пример использования
int main() {
    int width = 1024;
    int height = 1024;
    int size = width * height;

    // Выделение памяти на CPU
    int* currentGrid = (int*)malloc(size * sizeof(int));
    int* nextGrid = (int*)malloc(size * sizeof(int));

    // Инициализация текущей сетки (пример: случайные значения)
    for (int i = 0; i < size; i++) {
        currentGrid[i] = rand() % 2;
    }

    // Выделение памяти на GPU
    int* d_currentGrid;
    int* d_nextGrid;
    cudaMalloc(&d_currentGrid, size * sizeof(int));
    cudaMalloc(&d_nextGrid, size * sizeof(int));

    // Копирование данных на GPU
    copyGridToDevice(d_currentGrid, currentGrid, size);

    // Запуск ядра
    launchLifeKernel(d_currentGrid, d_nextGrid, width, height, 16);

    // Копирование результата обратно на CPU
    copyGridToHost(nextGrid, d_nextGrid, size);

    // Освобождение памяти
    free(currentGrid);
    free(nextGrid);
    cudaFree(d_currentGrid);
    cudaFree(d_nextGrid);

    return 0;
}
