package main

import (
	"net/http"
	"time"

	"github.com/elastic/go-elasticsearch/v8/esapi"
	"github.com/elastic/go-elasticsearch/v8/eshttp"
	"github.com/elastic/go-elasticsearch/v8/eshttp/transport"
	"github.com/elastic/go-elasticsearch/v8/eshttp/transport/proxy"
	"github.com/elastic/go-elasticsearch/v8/eshttp/transport/tls"
)

// TransportBuilder is a builder for configuring and creating an Elasticsearch HTTP transport.
type TransportBuilder struct {
	proxyURL     string
	proxyUser    string
	proxyPass    string
	disableProxy bool
	username     string
	password     string
	certFile     string
	keyFile      string
	headers      http.Header
	timeout      time.Duration
}

// Build creates and returns a configured Transport for Elasticsearch API requests.
func (b *TransportBuilder) Build() (*esapi.Transport, error) {
	// Configure proxy settings
	var proxyTransport *proxy.Transport
	if !b.disableProxy && b.proxyURL != "" {
		proxyTransport = proxy.New(b.proxyURL)
		if b.proxyUser != "" && b.proxyPass != "" {
			proxyTransport.SetBasicAuth(b.proxyUser, b.proxyPass)
		}
	}

	// Configure client authentication
	var authTransport *eshttp.BasicAuthTransport
	if b.username != "" && b.password != "" {
		authTransport = eshttp.NewBasicAuthTransport(b.username, b.password)
	}

	// Configure TLS settings
	var tlsTransport *tls.Transport
	if b.certFile != "" || b.keyFile != "" {
		tlsConfig, err := tls.New(b.certFile, b.keyFile)
		if err != nil {
			return nil, err
		}
		tlsTransport = tls.NewTransport(tlsConfig)
	}

	// Build the base transport
	var baseTransport *http.Transport
	if proxyTransport != nil {
		baseTransport = proxyTransport
	} else if authTransport != nil {
		baseTransport = authTransport
	} else if tlsTransport != nil {
		baseTransport = tlsTransport
	} else {
		// Fallback to default transport if no specific settings are provided
		baseTransport = &http.Transport{}
	}

	// Apply global timeout
	if b.timeout > 0 {
		baseTransport = &http.Transport{
			Proxy:               baseTransport.Proxy,
			DialContext:         baseTransport.DialContext,
			TLSClientConfig:     baseTransport.TLSClientConfig,
			ResponseHeaderTimeout: b.timeout,
			ExpectContinueTimeout: baseTransport.ExpectContinueTimeout,
			MaxIdleConns:        baseTransport.MaxIdleConns,
			IdleConnTimeout:     baseTransport.IdleConnTimeout,
			MaxIdleConnsPerHost: baseTransport.MaxIdleConnsPerHost,
			DisableKeepAlives:   baseTransport.DisableKeepAlives,
		}
	}

	// Create the final transport with headers
	transport := &esapi.Transport{
		Base: &eshttp.Transport{
			Base: baseTransport,
			Headers: b.headers,
		},
	}

	return transport, nil
}
