#include "knn_graph_wrapper.h"
#include <iomanip>
#include <random>
#include <chrono>

// CUDA ядро для вычисления расстояний между всеми парами точек
__global__ void compute_distances_kernel(
    float* data, int n_points, int dim, float* distances_out, int* indices_out) {
    int idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx >= n_points * n_points) return;

    int i = idx / n_points;
    int j = idx % n_points;
    if (i == j) return;

    float dist = 0.0f;
    for (int d = 0; d < dim; ++d) {
        float diff = data[i * dim + d] - data[j * dim + d];
        dist += diff * diff;
    }
    dist = sqrtf(dist);

    int pos = idx;
    distances_out[pos] = dist;
    indices_out[pos] = j;
}

// CUDA ядро для построения графа: сохраняем соседей для каждой точки
__global__ void build_knn_kernel(
    float* distances, int* indices, int n_points, int k, int* neighbors_out, float* distances_out) {
    int idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx >= n_points) return;

    // Инициализируем буфер для соседей
    int* temp_neighbors = new int[k];
    float* temp_distances = new float[k];

    // Поиск k ближайших соседей
    for (int i = 0; i < k; ++i) {
        temp_neighbors[i] = -1;
        temp_distances[i] = FLT_MAX;
    }

    for (int j = 0; j < n_points; ++j) {
        if (j != idx) {
            int pos = idx * n_points + j;
            float dist = distances[pos];
            if (dist < temp_distances[0]) {
                // Перемещаем все элементы вправо
                for (int i = k - 1; i > 0; --i) {
                    temp_neighbors[i] = temp_neighbors[i - 1];
                    temp_distances[i] = temp_distances[i - 1];
                }
                temp_neighbors[0] = j;
                temp_distances[0] = dist;
            } else if (dist < temp_distances[k - 1]) {
                // Добавляем только если меньше последнего
                for (int i = k - 1; i > 0; --i) {
                    temp_neighbors[i] = temp_neighbors[i - 1];
                    temp_distances[i] = temp_distances[i - 1];
                }
                temp_neighbors[0] = j;
                temp_distances[0] = dist;
            }
        }
    }

    // Сохраняем результат
    for (int i = 0; i < k; ++i) {
        neighbors_out[idx * k + i] = temp_neighbors[i];
        distances_out[idx * k + i] = temp_distances[i];
    }

    delete[] temp_neighbors;
    delete[] temp_distances;
}

// Конструктор
KNNGraphWrapper::KNNGraphWrapper(int max_points, int k) 
    : max_points_(max_points), k_(k) {
    AllocateGPU();
}

KNNGraphWrapper::~KNNGraphWrapper() {
    // Очистка GPU
    cudaFree(d_data_);
    cudaFree(d_distances_);
    cudaFree(d_indices_);
    cudaFree(d_neighbors_);
    cudaFree(d_distances_for_query_);
    cudaFree(d_query_result_);
    cudaFree(d_ground_truth_);

    // Очистка CPU
    for (auto& p : points_) {
        delete[] p.coords;
    }
    points_.clear();
    graph_nodes_.clear();
}

void KNNGraphWrapper::AllocateGPU() {
    cudaGetDeviceProperties(&prop_, 0);
    cudaStreamCreate(&stream_);

    // Размеры
    int total_dim = 32; // можно сделать динамическим
    size_t data_size = max_points_ * total_dim * sizeof(float);
    size_t distances_size = max_points_ * max_points_ * sizeof(float);
    size_t indices_size = max_points_ * max_points_ * sizeof(int);
    size_t neighbors_size = max_points_ * k_ * sizeof(int);
    size_t query_result_size = max_points_ * k_ * sizeof(int);

    cudaMalloc(&d_data_, data_size);
    cudaMalloc(&d_distances_, distances_size);
    cudaMalloc(&d_indices_, indices_size);
    cudaMalloc(&d_neighbors_, neighbors_size);
    cudaMalloc(&d_distances_for_query_, query_result_size);
    cudaMalloc(&d_query_result_, query_result_size);
}

void KNNGraphWrapper::CopyToGPU() {
    cudaMemcpyAsync(d_data_, &points_[0].coords[0], GetNumPoints() * 32 * sizeof(float), cudaMemcpyHostToDevice, stream_);
}

void KNNGraphWrapper::CopyFromGPU() {
    cudaMemcpyAsync(&points_[0].coords[0], d_data_, GetNumPoints() * 32 * sizeof(float), cudaMemcpyDeviceToHost, stream_);
}

void KNNGraphWrapper::BuildKNNOnGPU() {
    int n_points = GetNumPoints();
    int dim = 32;

    // Вычисление всех расстояний
    int threadsPerBlock = 256;
    int blocksPerGrid = (n_points * n_points + threadsPerBlock - 1) / threadsPerBlock;

    compute_distances_kernel<<<blocksPerGrid, threadsPerBlock>>>(
        d_data_, n_points, dim, d_distances_, d_indices_);

    // Построение графа
    blocksPerGrid = (n_points + threadsPerBlock - 1) / threadsPerBlock;
    build_knn_kernel<<<blocksPerGrid, threadsPerBlock>>>(
        d_distances_, d_indices_, n_points, k_, d_neighbors_, d_distances_for_query_);

    cudaDeviceSynchronize();
}

