void KNNGraphWrapper::EvaluateGraphImpl() {
    // Простая оценка: среднее количество соседей
    double sum = 0.0;
    for (const auto& node : graph_nodes_) {
        sum += static_cast<double>(node.neighbors.size());
    }
    return sum / graph_nodes_.size();
}

std::vector<int> KNNGraphWrapper::QueryImpl(int point_id, int k) {
    if (k == -1) k = k_;

    // Для простоты — просто возвращаем соседей из графа
    std::vector<int> result;
    if (point_id >= 0 && point_id < graph_nodes_.size()) {
        result = graph_nodes_[point_id].neighbors;
        if (result.size() > k) {
            result.resize(k);
        }
    }
    return result;
}

