    std::vector<std::pair<int, float>> result;
    auto neighbors = QueryImpl(point_id, k);
    for (size_t i = 0; i < neighbors.size(); ++i) {
        result.push_back({neighbors[i], graph_nodes_[point_id].distances[i]});
    }
    return result;
}

double KNNGraphWrapper::EvaluateGraph() {
    return EvaluateGraphImpl();
}

void KNNGraphWrapper::LoadData(const std::string& filename) {
    std::ifstream file(filename, std::ios::binary);
    if (!file.is_open()) {
        std::cerr << "Failed to open file: " << filename << std::endl;
        return;
    }

    int n_points, dim;
    file.read(reinterpret_cast<char*>(&n_points), sizeof(int));
    file.read(reinterpret_cast<char*>(&dim), sizeof(int));

    points_.resize(n_points);
    for (int i = 0; i < n_points; ++i) {
        points_[i].id = i;
        file.read(reinterpret_cast<char*>(points_[i].coords), dim * sizeof(float));
    }

    file.close();
    std::cout << "[INFO] Loaded " << n_points << " points with " << dim << " dimensions." << std::endl;
}

void KNNGraphWrapper::SaveData(const std::string& filename) {
    std::ofstream file(filename, std::ios::binary);
    if (!file.is_open()) {
        std::cerr << "Failed to open file: " << filename << std::endl;
        return;
    }

    int n_points = static_cast<int>(points_.size());
    int dim = 32; // предполагаемый размер

    file.write(reinterpret_cast<const char*>(&n_points), sizeof(int));
    file.write(reinterpret_cast<const char*>(&dim), sizeof(int));

    for (const auto& p : points_) {
