void KNNGraphWrapper::BuildGraph() {
    if (points_.empty()) {
        std::cerr << "[ERROR] No data loaded!" << std::endl;
        return;
    }

    CopyToGPU();
    BuildKNNOnGPU();

    // Скопировать результат обратно
    CopyFromGPU();

    // Строим граф на CPU
    graph_nodes_.resize(points_.size());
    for (int i = 0; i < points_.size(); ++i) {
        graph_nodes_[i].neighbors.clear();
        graph_nodes_[i].distances.clear();
        for (int j = 0; j < k_; ++j) {
            int neighbor_idx = d_neighbors_[i * k_ + j];
            float dist = d_distances_for_query_[i * k_ + j];
            graph_nodes_[i].neighbors.push_back(neighbor_idx);
            graph_nodes_[i].distances.push_back(dist);
        }
    }

    std::cout << "[INFO] Graph built with " << points_.size() << " nodes and " << k_ << " neighbors per node." << std::endl;
}

void KNNGraphWrapper::RefineGraph() {
    std::cout << "[INFO] Refining graph..." << std::endl;
    // Здесь можно добавить реальную логику уточнения
    // Например: удалить рёбра с малым весом, использовать фильтры
    for (auto& node : graph_nodes_) {
        // Пример: удалить соседей с расстоянием > 1.0
        auto it = node.neighbors.begin();
        while (it != node.neighbors.end()) {
            if (node.distances[it - node.neighbors.begin()] > 1.0f) {
                it = node.neighbors.erase(it);
                node.distances.erase(node.distances.begin() + (it - node.neighbors.begin()));
            } else {
                ++it;
            }
        }
    }
    std::cout << "[INFO] Graph refined." << std::endl;
