```go
package main

import (
	"crypto/tls"
	"fmt"
	"net/http"
	"net/url"
	"strings"

	"github.com/elastic/go-elasticsearch/v8/esapi"
)

// TransportBuilder представляет собой построитель транспорта для Elasticsearch.
type TransportBuilder struct {
	// Прокси-сервер с возможностью аутентификации
	ProxyURL *url.URL
	ProxyUser string
	ProxyPassword string

	// Отключение прокси (включая системные)
	DisableProxy bool

	// Учетные данные для аутентификации клиента в Elasticsearch
	Username string
	Password string

	// Проверка сертификата HTTPS
	InsecureSkipVerify bool

	// HTTP-заголовки, добавляемые ко всем запросам
	Headers http.Header

	// Глобальный тайм-аут для всех запросов
	Timeout time.Duration
}

// build создает Transport для отправки API-запросов в Elasticsearch.
func (t *TransportBuilder) build() (*http.Transport, error) {
	// Создаем базовый HTTP-клиент
	client := &http.Client{
		Timeout: t.Timeout,
	}

	// Настройка прокси
	if !t.DisableProxy {
		var proxyURL *url.URL
		if t.ProxyURL != nil {
			proxyURL = t.ProxyURL
		} else {
			// Используем системный прокси, если не задан явно
			proxyURL, _ = url.Parse("http://proxy.example.com:8080") // Замените на реальный прокси или используйте os.LookupEnv
		}

		// Если прокси задан и требует аутентификацию
		if t.ProxyUser != "" || t.ProxyPassword != "" {
			authString := ""
			if t.ProxyUser != "" && t.ProxyPassword != "" {
				authString = fmt.Sprintf("%s:%s", t.ProxyUser, t.ProxyPassword)
			}
			if authString != "" {
				proxyURL.User = url.UserPassword(authString)
			}
		}

		// Устанавливаем прокси
		client.Transport = &http.Transport{
			Proxy: func(req *http.Request) (*url.URL, error) {
				return proxyURL, nil
			},
		}
	}

	// Добавление заголовков
	if len(t.Headers) > 0 {
		client.Transport = &http.Transport{
			// Нужно использовать Custom RoundTripper для добавления заголовков
			RoundTripper: &customRoundTripper{
				transport: client.Transport,
				headers:   t.Headers,
			},
		}
	}

	// Настройка TLS-соединения
	tlsConfig := &tls.Config{
		InsecureSkipVerify: t.InsecureSkipVerify,
	}

	// Обновляем транспорт с TLS-настройками
	if client.Transport == nil {
		client.Transport = &http.Transport{
			TLSClientConfig: tlsConfig,
		}
	} else if tr, ok := client.Transport.(*http.Transport); ok {
		tr.TLSClientConfig = tlsConfig
	}

	// Добавляем аутентификацию через Basic Auth
	if t.Username != "" || t.Password != "" {
		client.Transport = &http.Transport{
			RoundTripper: &authRoundTripper{
				transport: client.Transport,
				username:  t.Username,
				password:  t.Password,
			},
		}
	}

	return client.Transport, nil
}

// customRoundTripper — обертка для добавления HTTP-заголовков к каждому запросу.
type customRoundTripper struct {
	transport http.RoundTripper
	headers   http.Header
}

func (c *customRoundTripper) RoundTrip(req *http.Request) (*http.Response, error) {
	for key, values := range c.headers {
		for _, value := range values {
			req.Header.Add(key, value)
		}
	}
	return c.transport.RoundTrip(req)
}

// authRoundTripper — обертка для добавления Basic Auth.
type authRoundTripper struct {
	transport http.RoundTripper
	username  string
	password  string
}

func (a *authRoundTripper) RoundTrip(req *http.Request) (*http.Response, error) {
	if a.username != "" && a.password != "" {
		authString := fmt.Sprintf("%s:%s", a.username, a.password)
		authBytes := []byte(authString)
		authBase64 := base64.StdEncoding.EncodeToString(authBytes)
		req.Header.Set("Authorization", "Basic "+authBase64)
	}
	return a.transport.RoundTrip(req)
}
