## ✅ 1. Общая структура

Файл: `game_of_life.cu`

```cpp
#include <cuda_runtime.h>
#include <device_launch_parameters.h>
#include <stdio.h>
#include <stdlib.h>
#include <vector>
#include <fstream>
#include <iostream>
#include <png.h>

// Размеры сетки (можно сделать параметром)
#define GRID_SIZE_X 512
#define GRID_SIZE_Y 512

// Правила Игры Жизни:
// 1. Любая живая клетка с менее чем двумя живыми соседями → умирает (одиночество).
// 2. Любая живая клетка с двумя или тремя живыми соседями → живёт.
// 3. Любая живая клетка с более чем тремя живыми соседями → умирает (перенаселение).
// 4. Любая мертвая клетка с ровно двумя живыми соседями → воскресает (рождение).

// Тип данных для клетки: 0 — мертва, 1 — жива
typedef unsigned char cell_t;

// Размер сетки в пикселях
constexpr int GRID_WIDTH = GRID_SIZE_X;
constexpr int GRID_HEIGHT = GRID_SIZE_Y;

// Смещения соседей (8 направлений)
const int dx[] = {-1, -1, -1, 0, 0, 1, 1, 1};
const int dy[] = {-1, 0, 1, -1, 1, -1, 0, 1};

// Глобальные переменные для хранения сеток
cell_t* d_grid_current;
cell_t* d_grid_next;

// Размеры сетки в байтах
size_t grid_size_bytes = GRID_WIDTH * GRID_HEIGHT * sizeof(cell_t);

// Функция для подсчета живых соседей (использует shared memory для оптимизации)
__device__ int count_neighbors(const cell_t* current_grid, int x, int y, int width, int height) {
    int count = 0;
    for (int i = 0; i < 8; ++i) {
        int nx = x + dx[i];
        int ny = y + dy[i];

        // Проверка границ
        if (nx >= 0 && nx < width && ny >= 0 && ny < height) {
            count += current_grid[nx + ny * width];
        }
    }
    return count;
}

// Ядро CUDA для обновления состояния
__global__ void gameOfLifeKernel(
    const cell_t* __restrict__ current_grid,
    cell_t* __restrict__ next_grid,
    int width, int height) {

    int x = blockIdx.x * blockDim.x + threadIdx.x;
    int y = blockIdx.y * blockDim.y + threadIdx.y;

    if (x >= width || y >= height) return;

    int neighbors = count_neighbors(current_grid, x, y, width, height);

    // Применяем правила
    if (current_grid[x + y * width] == 1) {
        next_grid[x + y * width] = (neighbors == 2 || neighbors == 3) ? 1 : 0;
    } else {
        next_grid[x + y * width] = (neighbors == 3) ? 1 : 0;
    }
}

// Функция для копирования данных на GPU
void copyToGPU(cell_t* h_grid, cell_t** d_grid_ptr) {
    cudaMalloc(d_grid_ptr, grid_size_bytes);
    cudaMemcpy(*d_grid_ptr, h_grid, grid_size_bytes, cudaMemcpyHostToDevice);
}

// Функция для копирования данных с GPU
void copyFromGPU(cell_t* h_grid, cell_t* d_grid) {
    cudaMemcpy(h_grid, d_grid, grid_size_bytes, cudaMemcpyDeviceToHost);
}

// Функция для сохранения изображения в PNG
bool savePNG(const std::string& filename, const cell_t* data, int width, int height) {
    FILE* fp = fopen(filename.c_str(), "wb");
    if (!fp) return false;

    png_structp png = png_create_write_struct(PNG_LIBPNG_VER_STRING, NULL, NULL, NULL);
    png_infop info = png_create_info_struct(png);
    if (!png || !info) {
        fclose(fp);
        return false;
    }

    png_init_io(png, fp);

    png_set_IHDR(png, info, width, height, 8, PNG_COLOR_TYPE_GRAY, PNG_INTERLACE_NONE,
                 PNG_COMPRESSION_TYPE_DEFAULT, PNG_FILTER_TYPE_DEFAULT);

    png_write_info(png, info);

    png_bytep row[1];
    for (int y = 0; y < height; ++y) {
        row[0] = (png_byte*)data + y * width;
        png_write_row(png, row);
    }

    png_write_end(png, info);
    png_destroy_write_struct(&png, &info);
    fclose(fp);
    return true;
}

// Функция для выполнения одного шага эволюции
void evolveOneStep(cell_t* h_grid) {
    // Выделить память на GPU
    copyToGPU(h_grid, &d_grid_current);

    // Аллоцировать память для следующего состояния
    cudaMalloc(&d_grid_next, grid_size_bytes);

    // Запуск ядра
    dim3 blockSize(16, 16);
    dim3 gridSize((GRID_WIDTH + blockSize.x - 1) / blockSize.x,
                  (GRID_HEIGHT + blockSize.y - 1) / blockSize.y);

    gameOfLifeKernel<<<gridSize, blockSize>>>(d_grid_current, d_grid_next, GRID_WIDTH, GRID_HEIGHT);

    // Копирование обратно на CPU
    copyFromGPU(h_grid, d_grid_next);

    // Очистка
    cudaFree(d_grid_current);
    cudaFree(d_grid_next);
}

// Регрессионный тест: сравнение текущего состояния с эталонным
bool regressionTest(const cell_t* h_grid, const cell_t* ref_grid) {
    for (int i = 0; i < GRID_WIDTH * GRID_HEIGHT; ++i) {
        if (h_grid[i] != ref_grid[i]) {
            printf("Regression test failed at position %d\n", i);
            return false;
        }
    }
    return true;
}

// Функция для запуска симуляции на всех доступных GPU
void runOnAllGPUs() {
    int device_count;
    cudaGetDeviceCount(&device_count);

    for (int dev = 0; dev < device_count; ++dev) {
        printf("Running on GPU #%d...\n", dev);
        cudaSetDevice(dev);

        // Создаем начальную сетку (пример: живые клетки по центру)
        cell_t* h_grid = new cell_t[GRID_WIDTH * GRID_HEIGHT];
        for (int i = 0; i < GRID_WIDTH * GRID_HEIGHT; ++i) {
            h_grid[i] = 0;
        }

        // Создаем тестовый паттерн (например, "блестящий" блок 3x3)
        for (int x = 100; x < 103; ++x) {
            for (int y = 100; y < 103; ++y) {
                h_grid[x + y * GRID_WIDTH] = 1;
            }
        }

        // Сохраняем начальное состояние
        savePNG("initial_state.png", h_grid, GRID_WIDTH, GRID_HEIGHT);

        // Запуск эволюции
        for (int step = 0; step < 5; ++step) {
            evolveOneStep(h_grid);
            printf("Step %d completed.\n", step + 1);

            // Сохраняем каждое состояние
            std::string filename = "step_" + std::to_string(step + 1) + ".png";
            if (!savePNG(filename, h_grid, GRID_WIDTH, GRID_HEIGHT)) {
                printf("Failed to save image: %s\n", filename.c_str());
            }

            // Регрессионный тест (на каждом шаге сравниваем с эталоном)
            // Для простоты создадим эталонный файл для первого шага
            if (step == 0) {
                // Эталонное состояние после 1 шага
                cell_t* ref_grid = new cell_t[GRID_WIDTH * GRID_HEIGHT];
                for (int i = 0; i < GRID_WIDTH * GRID_HEIGHT; ++i) {
                    ref_grid[i] = h_grid[i]; // Можно загрузить из файла или вычислить
                }
                if (!regressionTest(h_grid, ref_grid)) {
                    printf("Regression test failed!\n");
                    delete[] ref_grid;
                    break;
                }
                delete[] ref_grid;
            }
        }

        delete[] h_grid;
        printf("Finished on GPU #%d\n", dev);
    }
}

// Главная функция
int main() {
    printf("Starting Conway's Game of Life simulation with MAPS-Multi support...\n");

    try {
        runOnAllGPUs();
    } catch (const std::exception& e) {
        fprintf(stderr, "Error: %s\n", e.what());
        return EXIT_FAILURE;
    }

    printf("Simulation completed successfully!\n");
    return EXIT_SUCCESS;
}
```

