## 🔧 Реализация класса `KNNGraphWrapper`

Вот полный код реализации:

```cpp
#include <cuda_runtime.h>
#include <device_launch_parameters.h>
#include <vector>
#include <fstream>
#include <iostream>
#include <random>
#include <iomanip>
#include <algorithm>

// Просто для примера: вы можете заменить на более продвинутые структуры
struct Point {
    float* coords;
    int dim;
    int id;
};

struct Edge {
    int from;
    int to;
    float distance;
};

struct Graph {
    std::vector<Point> points;
    std::vector<Edge> edges;
    int n_points;
    int k;
};

// Константы
const int MAX_POINTS = 10000;
const int DEFAULT_K = 5;

// CUDA ядра
__global__ void compute_knn_kernel(
    const float* __restrict__ points,
    const int n_points,
    const int dim,
    const int k,
    int* __restrict__ neighbors,
    float* __restrict__ distances)
{
    int idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx >= n_points) return;

    // Инициализируем массивы
    for (int i = 0; i < k; ++i) {
        neighbors[idx * k + i] = -1;
        distances[idx * k + i] = FLT_MAX;
    }

    // Для каждого соседа
    for (int j = 0; j < n_points; ++j) {
        if (j == idx) continue;

        float dist = 0.0f;
        for (int d = 0; d < dim; ++d) {
            float diff = points[idx * dim + d] - points[j * dim + d];
            dist += diff * diff;
        }
        dist = sqrtf(dist);

        // Находим позицию для вставки
        int pos = 0;
        while (pos < k && distances[idx * k + pos] < dist) {
            ++pos;
        }

        if (pos < k) {
            // Вставляем
            for (int i = k - 1; i > pos; --i) {
                neighbors[idx * k + i] = neighbors[idx * k + i - 1];
                distances[idx * k + i] = distances[idx * k + i - 1];
            }
            neighbors[idx * k + pos] = j;
            distances[idx * k + pos] = dist;
        }
    }
}

// Конструктор
KNNGraphWrapper::KNNGraphWrapper(int max_points, int k) : k(k), n_points(0) {
    this->max_points = max_points;
    stream = 0;
    allocate_gpu_memory();
}

KNNGraphWrapper::~KNNGraphWrapper() {
    free_gpu_memory();
}

void KNNGraphWrapper::allocate_gpu_memory() {
    cudaMalloc(&d_points, max_points * 32 * sizeof(float)); // предположим 32-размерное векторное пространство
    cudaMalloc(&d_point_ids, max_points * sizeof(int));
    cudaMalloc(&d_neighbors, max_points * k * sizeof(int));
    cudaMalloc(&d_distances, max_points * k * sizeof(float));
    d_points_size = max_points * 32 * sizeof(float);
}

void KNNGraphWrapper::free_gpu_memory() {
    cudaFree(d_points);
    cudaFree(d_point_ids);
    cudaFree(d_neighbors);
    cudaFree(d_distances);
}

void KNNGraphWrapper::copy_to_device() {
    cudaMemcpyAsync(d_points, h_points.data(), d_points_size, cudaMemcpyHostToDevice, stream);
    cudaMemcpyAsync(d_point_ids, h_point_ids.data(), n_points * sizeof(int), cudaMemcpyHostToDevice, stream);
}

void KNNGraphWrapper::copy_from_device() {
    cudaMemcpyAsync(h_points.data(), d_points, d_points_size, cudaMemcpyDeviceToHost, stream);
    cudaMemcpyAsync(h_point_ids.data(), d_point_ids, n_points * sizeof(int), cudaMemcpyDeviceToHost, stream);
}

void KNNGraphWrapper::load_data(const std::string& filename) {
    std::ifstream file(filename, std::ios::binary);
    if (!file.is_open()) {
        throw std::runtime_error("Cannot open data file: " + filename);
    }

    file.read(reinterpret_cast<char*>(h_points.data()), n_points * 32 * sizeof(float));
    file.read(reinterpret_cast<char*>(h_point_ids.data()), n_points * sizeof(int));

    n_points = h_point_ids.size();
    copy_to_device();
}

void KNNGraphWrapper::save_data(const std::string& filename) {
    std::ofstream file(filename, std::ios::binary);
    if (!file.is_open()) {
        throw std::runtime_error("Cannot write to file: " + filename);
    }

    file.write(reinterpret_cast<const char*>(h_points.data()), n_points * 32 * sizeof(float));
    file.write(reinterpret_cast<const char*>(h_point_ids.data()), n_points * sizeof(int));
}

void KNNGraphWrapper::build_graph() {
    // Запуск CUDA ядра
    int blockSize = 256;
    int gridSize = (n_points + blockSize - 1) / blockSize;
    compute_knn_kernel<<<gridSize, blockSize>>>(d_points, n_points, 32, k, d_neighbors, d_distances);
    cudaDeviceSynchronize();
}

void KNNGraphWrapper::refine_graph() {
    // Здесь можно добавить логику уточнения: например, удаление дублей, фильтрация по расстоянию, etc.
    std::cout << "[INFO] Refining graph...\n";
}

void KNNGraphWrapper::generate_ground_truth() {
    std::mt19937 rng(std::time(nullptr));
    std::uniform_int_distribution<> dist(0, n_points - 1);
    ground_truth.clear();
    for (int i = 0; i < 100; ++i) { // 100 эталонных запросов
        int query_idx = dist(rng);
        std::vector<int> true_neighbors;
        for (int j = 0; j < k; ++j) {
            true_neighbors.push_back(query_idx + j % n_points); // просто пример
        }
        ground_truth.push_back(true_neighbors);
    }
    std::cout << "[INFO] Generated " << ground_truth.size() << " ground truth samples.\n";
}

double KNNGraphWrapper::evaluate_graph() {
    double accuracy = 0.0;
    int total = 0;

    for (const auto& gt : ground_truth) {
        auto query_result = query_knn(gt[0], k);
        int correct = 0;
        for (int i = 0; i < k; ++i) {
            if (std::find(gt.begin(), gt.end(), query_result[i]) != gt.end()) {
                correct++;
            }
        }
        accuracy += static_cast<double>(correct) / k;
        total++;
    }

    return accuracy / total;
}

std::vector<int> KNNGraphWrapper::query_knn(int point_id, int k) {
    std::vector<int> result;
    result.resize(k);
    std::vector<float> distances(k);

    query_knn_on_gpu(point_id, k, result, distances);

    if (debug_mode) {
        std::cout << "[DEBUG] Query #" << point_id << ": ";
        for (int i = 0; i < k; ++i) {
            std::cout << result[i] << " (" << std::fixed << std::setprecision(4) << distances[i] << ") ";
        }
        std::cout << "\n";
    }

    return result;
}

std::vector<std::pair<double, int>> KNNGraphWrapper::query_knn_with_distances(int point_id, int k) {
    std::vector<std::pair<double, int>> result;
    result.reserve(k);

    query_knn_on_gpu(point_id, k, result, result); // передача двойного указателя

    if (debug_mode) {
        std::cout << "[DEBUG] Query #" << point_id << " with distances:\n";
        for (auto& p : result) {
            std::cout << "(" << p.second << ", " << p.first << ")\n";
        }
    }

    return result;
}

void KNNGraphWrapper::reset_graph_subset(int subset_size) {
    if (subset_size <= 0 || subset_size > n_points) {
        throw std::invalid_argument("Subset size must be between 1 and n_points");
    }

    // Удаляем старые данные
    h_points.clear();
    h_point_ids.clear();

    // Генерируем новые случайные данные
    std::mt19937 rng(std::time(nullptr));
    std::uniform_real_distribution<float> dist(-1.0f, 1.0f);

    h_points.resize(subset_size, std::vector<float>(32));
    h_point_ids.resize(subset_size);

    for (int i = 0; i < subset_size; ++i) {
        h_point_ids[i] = i;
        for (int d = 0; d < 32; ++d) {
            h_points[i][d] = dist(rng);
        }
    }

    n_points = subset_size;
    copy_to_device();
    std::cout << "[INFO] Reset graph to subset of size " << subset_size << ".\n";
}

void KNNGraphWrapper::enable_debug_mode() {
    debug_mode = true;
    std::cout << "[DEBUG MODE ENABLED]\n";
}

void KNNGraphWrapper::disable_debug_mode() {
    debug_mode = false;
    std::cout << "[DEBUG MODE DISABLED]\n";
}

void KNNGraphWrapper::print_debug_info() {
    std::cout << "=== DEBUG INFO ===\n";
    std::cout << "Points: " << n_points << "\n";
    std::cout << "K: " << k << "\n";
    std::cout << "GPU Memory Allocated: " << d_points_size / (1024 * 1024) << " MB\n";
    std::cout << "Debug Mode: " << (debug_mode ? "ON" : "OFF") << "\n";
}

// Ядро запроса (вызов из host)
void KNNGraphWrapper::query_knn_on_gpu(int point_id, int k, std::vector<int>& neighbors, std::vector<float>& distances) {
    // Это просто копия из build_graph, но для конкретной точки
    // В реальном приложении можно использовать другую версию ядра
    // Здесь мы используем тот же подход

    int blockSize = 256;
    int gridSize = (n_points + blockSize - 1) / blockSize;

    compute_knn_kernel<<<gridSize, blockSize>>>(d_points, n_points, 32, k, d_neighbors, d_distances);
    cudaDeviceSynchronize();

    // Копируем результат обратно
    cudaMemcpy(neighbors.data(), d_neighbors + point_id * k, k * sizeof(int), cudaMemcpyDeviceToHost);
    cudaMemcpy(distances.data(), d_distances + point_id * k, k * sizeof(float), cudaMemcpyDeviceToHost);
}
```

