#include <vector>
#include <iostream>
#include <iomanip>
#include "game_of_life_kernel.cu"

using namespace std;

// Генератор тестовой сетки (пример: "блок" из 5x5)
vector<vector<int>> generate_test_grid() {
    vector<vector<int>> grid(5, vector<int>(5, 0));
    grid[2][2] = 1; // Центральная клетка
    grid[2][3] = 1;
    grid[3][2] = 1;
    grid[3][3] = 1;
    return grid;
