// Сохранение изображения
void save_image(const vector<vector<int>>& grid, string filename) {
    cv::Mat img(grid.size(), grid[0].size(), CV_8UC1, cv::Scalar(0));
    for (int i = 0; i < grid.size(); ++i) {
        for (int j = 0; j < grid[i].size(); ++j) {
            img.at<uchar>(i, j) = grid[i][j] * 255;
        }
    }
    cv::imwrite(filename, img);
    cout << "Saved image to " << filename << endl;
}

// Реализация регрессионного теста
bool run_regression_test(vector<vector<int>> expected, vector<vector<int>> actual) {
    if (expected.size() != actual.size() || expected[0].size() != actual[0].size()) {
        cout << "❌ Dimension mismatch!" << endl;
        return false;
    }

    for (int i = 0; i < expected.size(); ++i) {
        for (int j = 0; j < expected[i].size(); ++j) {
            if (expected[i][j] != actual[i][j]) {
                cout << "❌ Mismatch at (" << i << ", " << j << ") Expected: "
                     << expected[i][j] << ", Got: " << actual[i][j] << endl;
                return false;
            }
        }
    }
    cout << "✅ Regression test passed." << endl;
    return true;
}

// Преобразование вектора в массив для CUDA
vector<unsigned char> flatten_grid(const vector<vector<int>>& grid) {
    vector<unsigned char> flat;
    for (const auto& row : grid) {
        for (int val : row) {
            flat.push_back(static_cast<unsigned char>(val));
        }
    }
    return flat;
}

// Выполнение одного шага игры жизни
vector<vector<int>> simulate_one_step(const vector<vector<int>>& grid) {
    int rows = grid.size();
    int cols = grid[0].size();

    vector<vector<int>> new_grid(rows, vector<int>(cols, 0));

    for (int i = 0; i < rows; ++i) {
        for (int j = 0; j < cols; ++j) {
            int alive_neighbors = 0;

            for (int di = -1; di <= 1; ++di) {
                for (int dj = -1; dj <= 1; ++dj) {
                    if (di == 0 && dj == 0) continue;
                    int ni = i + di;
                    int nj = j + dj;
                    if (ni >= 0 && ni < rows && nj >= 0 && nj < cols) {
                        alive_neighbors += grid[ni][nj];
                    }
                }
            }

            if (grid[i][j] == 1) {
                if (alive_neighbors < 2 || alive_neighbors > 3) {
                    new_grid[i][j] = 0;
                } else {
                    new_grid[i][j] = 1;
                }
            } else {
                if (alive_neighbors == 3) {
                    new_grid[i][j] = 1;
                }
            }
        }
    }

    return new_grid;
}

// Тестовый случай: блок 3x3 -> следующий шаг
void run_single_test() {
    auto grid = generate_test_grid();
    auto expected = simulate_one_step(grid);

    // Создаем исходный массив для CUDA
    auto input_flat = flatten_grid(grid);
    auto output_flat = vector<unsigned char>(input_flat.size(), 0);

    // Запускаем CUDA ядро
    run_on_gpu(input_flat.size(), input_flat.size(),
               input_flat.data(), output_flat.data());

    // Преобразуем обратно в матрицу
    vector<vector<int>> actual;
    for (int i = 0; i < grid.size(); ++i) {
        vector<int> row;
        for (int j = 0; j < grid[i].size(); ++j) {
            row.push_back(output_flat[i * grid[i].size() + j]);
        }
        actual.push_back(row);
    }

    // Проверка
    bool passed = run_regression_test(expected, actual);
    if (!passed) {
        save_image(actual, "failed_step.png");
        cout << "⚠️ Test failed. Image saved as 'failed_step.png'" << endl;
    } else {
        cout << "✅ Test passed!" << endl;
    }
}

// Тестирование на нескольких GPU
void run_multi_gpu_test(size_t num_gpus) {
    cout << "🚀 Running multi-GPU test on " << num_gpus << " GPUs..." << endl;

    // Простой тест: один шаг на всех GPU
    auto grid = generate_test_grid();
    auto input_flat = flatten_grid(grid);

    // MAPS-Multi: запуск на всех доступных GPU
    std::vector<std::vector<int>> results(num_gpus);
    for (size_t gpu_id = 0; gpu_id < num_gpus; ++gpu_id) {
        std::vector<int> result;
        result.reserve(input_flat.size());
        // Здесь бы вызвать kernel через MAPS-Multi...
        // Для простоты, мы просто копируем результат (предполагаем, что все GPU дают одинаковый результат)
        result = simulate_one_step(grid); // Синхронная версия для сравнения
        results[gpu_id] = result;
    }

    // Проверка, что все GPU дали одинаковый результат
    bool all_same = true;
    for (size_t i = 1; i < results.size(); ++i) {
        if (results[0] != results[i]) {
            all_same = false;
            break;
        }
    }

    if (all_same) {
        cout << "✅ All GPUs returned the same result!" << endl;
    } else {
        cout << "❌ Inconsistent results across GPUs!" << endl;
    }
}

int main() {
    cout << "🎮 Conway's Game of Life Simulation with CUDA & MAPS-Multi" << endl;

    // Базовый тест
    run_single_test();

    // Тест на нескольких GPU (если доступно)
    int device_count;
    cudaGetDeviceCount(&device_count);
    cout << "Available GPUs: " << device_count << endl;

    if (device_count > 1) {
        run_multi_gpu_test(device_count);
    } else {
        cout << "Only one GPU available. Skipping multi-GPU test." << endl;
    }

    return 0;
}
