namespace App\Rules;

use Illuminate\Contracts\Validation\Rule;
use Illuminate\Support\Carbon;

class AllowEditExceptStartDate implements Rule
{
    public function passes($attribute, $value)
    {
        // Если это не поле start_date — разрешено
        if ($attribute !== 'start_date') {
            return true;
        }

        // Если поле start_date — проверяем дату
