#include <iostream>
#include <vector>
#include <fstream>
#include <sstream>
#include <iomanip>
#include <algorithm>
#include <cuda_runtime.h>
#include <device_launch_parameters.h>

// Для простоты используем только float4 для представления точек
struct Point {
    float x, y, z, w;
    __host__ __device__ Point() : x(0), y(0), z(0), w(0) {}
    __host__ __device__ Point(float x_, float y_, float z_, float w_) 
        : x(x_), y(y_), z(z_), w(w_) {}
};

// Класс для хранения индексов соседей
struct Neighbor {
    int index;
    float distance;
    __host__ __device__ Neighbor(int idx = -1, float dist = 0.0f) 
        : index(idx), distance(dist) {}
};

// Класс для хранения графа KNN (на GPU)
class KnnGraphGPU {
public:
    struct GraphNode {
        int id; // индекс точки
        std::vector<int> neighbors; // список соседей
        float* distances; // массив расстояний к соседям
        int neighbor_count;

        __host__ __device__ GraphNode(int id_ = -1) : id(id_), neighbor_count(0), distances(nullptr) {}
    };

    // Массив узлов графа (на GPU)
    GraphNode* nodes;
    int num_nodes;
    int max_neighbors;

    // Конструктор
    __host__ KnnGraphGPU(int n, int k) : num_nodes(n), max_neighbors(k) {
        cudaMalloc(&nodes, n * sizeof(GraphNode));
        for (int i = 0; i < n; ++i) {
            nodes[i].distances = nullptr;
            nodes[i].neighbors.clear();
        }
    }

    // Деструктор
    __host__ ~KnnGraphGPU() {
        if (nodes) {
            cudaFree(nodes);
        }
    }

    // Получить указатель на узел
    __host__ __device__ GraphNode& getNode(int i) {
        return nodes[i];
    }

    // Добавить ребро (индекс соседа + расстояние)
    __host__ void addEdge(int node_id, int neighbor_id, float dist) {
        if (node_id >= num_nodes || neighbor_id >= num_nodes) return;
        if (nodes[node_id].neighbor_count >= max_neighbors) return;

        nodes[node_id].neighbors.push_back(neighbor_id);
        if (nodes[node_id].distances == nullptr) {
            cudaMalloc(&nodes[node_id].distances, max_neighbors * sizeof(float));
        }
        nodes[node_id].distances[nodes[node_id].neighbor_count] = dist;
        nodes[node_id].neighbor_count++;
    }

    // Получить количество соседей у узла
    __host__ int getNeighborCount(int node_id) {
        return nodes[node_id].neighbor_count;
    }

    // Получить соседей узла
    __host__ const std::vector<int>& getNeighbors(int node_id) {
        return nodes[node_id].neighbors;
    }

    // Получить расстояния до соседей
    __host__ const float* getDistances(int node_id) {
        return nodes[node_id].distances;
    }

    // Сохранить граф в файл (просто текстовый формат)
    __host__ void saveToFile(const std::string& filename) {
        std::ofstream file(filename);
        if (!file.is_open()) {
            std::cerr << "Не удалось открыть файл для записи: " << filename << std::endl;
            return;
        }

        file << num_nodes << " " << max_neighbors << "\n";
        for (int i = 0; i < num_nodes; ++i) {
            file << i << ": ";
            for (int j = 0; j < nodes[i].neighbor_count; ++j) {
                file << nodes[i].neighbors[j] << "(" << nodes[i].distances[j] << ") ";
            }
            file << "\n";
        }
        file.close();
    }

    // Загрузить граф из файла
    __host__ bool loadFromFile(const std::string& filename) {
        std::ifstream file(filename);
        if (!file.is_open()) {
            std::cerr << "Не удалось открыть файл для чтения: " << filename << std::endl;
            return false;
        }

        file >> num_nodes >> max_neighbors;
        if (num_nodes <= 0) return false;

        // Очистка старого графа
        this->~KnnGraphGPU();
        new(this) KnnGraphGPU(num_nodes, max_neighbors);

        for (int i = 0; i < num_nodes; ++i) {
            file >> std::ws; // пропуск пробельных символов
            file >> i;
            int count = 0;
            while (count < max_neighbors && !file.eof()) {
                int neighbor;
                float dist;
                file >> neighbor >> "(" >> dist >> ")";
                addEdge(i, neighbor, dist);
                count++;
            }
        }
        return true;
    }
};

// Функция для вычисления евклидова расстояния между двумя точками
__device__ float euclidean_distance(const Point& a, const Point& b) {
    float dx = a.x - b.x;
    float dy = a.y - b.y;
    float dz = a.z - b.z;
    float dw = a.w - b.w;
    return sqrtf(dx*dx + dy*dy + dz*dz + dw*dw);
}

// Функция для поиска k ближайших соседей (CPU версия для демонстрации)
std::vector<Neighbor> find_k_nearest(const std::vector<Point>& points, int query_idx, int k) {
    std::vector<Neighbor> neighbors;
    for (int i = 0; i < points.size(); ++i) {
        if (i != query_idx) {
            float dist = euclidean_distance(points[query_idx], points[i]);
            neighbors.emplace_back(i, dist);
        }
    }
    std::sort(neighbors.begin(), neighbors.end(), [](const Neighbor& a, const Neighbor& b) {
        return a.distance < b.distance;
    });
    if (neighbors.size() > k) neighbors.resize(k);
    return neighbors;
}

// Класс для управления данными и графикой
class KnnGraphManager {
private:
    std::vector<Point> data_points;
    KnnGraphGPU graph;
    bool debug_mode = false;
    bool verbose = false;

public:
    KnnGraphManager(int capacity = 1000, int k = 10) : graph(capacity, k) {}

    // Загрузка данных
    void loadData(const std::string& filepath) {
        std::ifstream file(filepath);
        if (!file.is_open()) {
            std::cerr << "Не удалось открыть файл: " << filepath << std::endl;
            return;
        }

        std::string line;
        while (std::getline(file, line)) {
            if (line.empty()) continue;
            std::stringstream ss(line);
            float x, y, z, w;
            ss >> x >> y >> z >> w;
            data_points.emplace_back(x, y, z, w);
        }
        std::cout << "Загружено " << data_points.size() << " точек." << std::endl;
    }

    // Построение графа KNN (CPU версия — можно заменить на CUDA)
    void buildGraph(int k) {
        std::cout << "Построение графа KNN..." << std::endl;
        for (size_t i = 0; i < data_points.size(); ++i) {
            auto neighbors = find_k_nearest(data_points, static_cast<int>(i), k);
            for (const auto& nb : neighbors) {
                graph.addEdge(static_cast<int>(i), nb.index, nb.distance);
            }
        }
        std::cout << "Граф построен." << std::endl;
    }

    // Уточнение графа (например, добавление новых связей на основе текущих)
    void refineGraph(int refinement_rounds = 1) {
        std::cout << "Уточнение графа (" << refinement_rounds << " раундов)..." << std::endl;
        for (int round = 0; round < refinement_rounds; ++round) {
            for (size_t i = 0; i < data_points.size(); ++i) {
