## ✅ Правильная реализация с использованием `http.RoundTripper`

Ниже — исправленная версия, которая использует `http.RoundTripper` для корректной настройки прокси, аутентификации и заголовков:

```go
package main

import (
	"crypto/tls"
	"encoding/base64"
	"fmt"
	"net/http"
	"net/http/httputil"
	"net/url"
	"strings"
	"time"
)

// TransportBuilder — конструктор транспорта для Elasticsearch
type TransportBuilder struct {
	// Прокси-сервер с возможностью базовой аутентификации
	ProxyURL *url.URL
	ProxyUser string
	ProxyPassword string

	// Отключение прокси (включая системные)
	DisableProxy bool

	// Учетные данные клиента для аутентификации в Elasticsearch
	Username string
	Password string

	// Проверка сертификата HTTPS
	InsecureSkipVerify bool

	// HTTP-заголовки, добавляемые ко всем запросам
	Headers http.Header

	// Глобальный тайм-аут для всех запросов
	GlobalTimeout time.Duration
}

// build — метод для создания транспорта для отправки API-запросов в Elasticsearch
func (tb *TransportBuilder) build() (*http.Client, error) {
	// Создаем базовый HTTP-клиент
	client := &http.Client{
		Timeout: tb.GlobalTimeout,
		Transport: &roundTripper{
			ProxyURL:           tb.ProxyURL,
			ProxyUser:          tb.ProxyUser,
			ProxyPassword:      tb.ProxyPassword,
			DisableProxy:       tb.DisableProxy,
			Username:           tb.Username,
			Password:           tb.Password,
			InsecureSkipVerify: tb.InsecureSkipVerify,
			Headers:            tb.Headers,
		},
	}

	return client, nil
}

// roundTripper — обертка вокруг Transport для настройки прокси, аутентификации и заголовков
type roundTripper struct {
	ProxyURL           *url.URL
	ProxyUser          string
	ProxyPassword      string
	DisableProxy       bool
	Username           string
	Password           string
	InsecureSkipVerify bool
	Headers            http.Header
}

func (rt *roundTripper) RoundTrip(req *http.Request) (*http.Response, error) {
	// Копируем оригинальный запрос
	reqCopy := new(http.Request)
	*reqCopy = *req

	// Добавляем глобальные заголовки
	if rt.Headers != nil {
		for key, values := range rt.Headers {
			for _, value := range values {
				reqCopy.Header.Add(key, value)
			}
		}
	}

	// Добавляем Basic Auth если указаны учетные данные
	if rt.Username != "" && rt.Password != "" {
		reqCopy.SetBasicAuth(rt.Username, rt.Password)
	}

	// Настройка прокси
	if !rt.DisableProxy && rt.ProxyURL != nil {
		// Если есть прокси и нужно аутентифицироваться через него
		if rt.ProxyUser != "" || rt.ProxyPassword != "" {
			authString := fmt.Sprintf("%s:%s", rt.ProxyUser, rt.ProxyPassword)
			encodedAuth := base64.StdEncoding.EncodeToString([]byte(authString))
			proxyURL := rt.ProxyURL.String()
			// Формат: http://user:pass@host:port
			if strings.HasPrefix(proxyURL, "http://") {
				proxyURL = "http://" + encodedAuth + "@" + strings.TrimPrefix(proxyURL, "http://")
			} else if strings.HasPrefix(proxyURL, "https://") {
				proxyURL = "https://" + encodedAuth + "@" + strings.TrimPrefix(proxyURL, "https://")
			}
			proxyURL, err := url.Parse(proxyURL)
			if err != nil {
				return nil, fmt.Errorf("ошибка при парсинге прокси URL: %w", err)
			}
			reqCopy.Header.Set("Proxy-Authorization", "Basic "+encodedAuth)
			reqCopy.URL.Scheme = "http" // Необходимо для прокси
			reqCopy.URL.Host = proxyURL.Host
		}
		// Используем стандартный прокси
		reqCopy.Proxied = true
		reqCopy.Proxy = http.ProxyURL(rt.ProxyURL)
	}

	// Настройка TLS
	if rt.InsecureSkipVerify {
		tr := &http.Transport{
			TLSClientConfig: &tls.Config{InsecureSkipVerify: true},
		}
		reqCopy.TLSClientConfig = tr.TLSClientConfig
	}

	// Выполняем запрос
	resp, err := http.DefaultTransport.RoundTrip(reqCopy)
	if err != nil {
		return nil, err
	}

	return resp, nil
}
