    var found = assemblies.SingleOrDefault(a => a.GetName().Name == name);

    if (found != null)
        return found;

    // Попробуем загрузить из файла
    var basePath = AppDomain.CurrentDomain.BaseDirectory;
    var path = Path.Combine(basePath, $"{name}.dll");
    if (File.Exists(path))
    {
        return Assembly.LoadFrom(path);
    }

    // Или попробуй найти по всему пути, если сборка в подкаталогах
    foreach (var dir in new[] { basePath, Path.GetDirectoryName(basePath) })
    {
        var fullPath = Path.Combine(dir, $"{name}.dll");
        if (File.Exists(fullPath))
        {
            return Assembly.LoadFrom(fullPath);
        }
    }

    return null;
}
