## 🧪 Регрессионные тесты и запуск

```cpp
bool runRegressionTest(const std::string& testName, const GridState& state) {
    // Создание копии текущего состояния для сравнения
    int* hostCurrent = new int[state.width * state.height];
    cudaMemcpy(hostCurrent, state.d_grid, state.width * state.height * sizeof(int), cudaMemcpyDeviceToHost);

    // Запуск ядра
    dim3 blockSize(BLOCK_SIZE, BLOCK_SIZE);
    dim3 gridSize((state.width + blockSize.x - 1) / blockSize.x, (state.height + blockSize.y - 1) / blockSize.y);

    gameOfLifeKernel<<<gridSize, blockSize>>>(state.d_grid, state.d_next_grid, state.width, state.height);

    cudaError_t err = cudaGetLastError();
    if (err != cudaSuccess) {
        std::cerr << "Ошибка в ядре: " << cudaGetErrorString(err) << std::endl;
        delete[] hostCurrent;
        return false;
    }

    cudaDeviceSynchronize();

    // Получаем результат
    int* hostNext = new int[state.width * state.height];
    cudaMemcpy(hostNext, state.d_next_grid, state.width * state.height * sizeof(int), cudaMemcpyDeviceToHost);

    // Сравнение с эталонным (например, заранее известное состояние после шага)
    bool passed = true;
    for (int i = 0; i < state.width * state.height; ++i) {
        if (hostCurrent[i] != hostNext[i]) {
            passed = false;
            break;
        }
    }

    delete[] hostCurrent;
    delete[] hostNext;

    if (!passed) {
        std::cout << "❌ Тест " << testName << " провален!" << std::endl;
        saveImage("test_" + testName + "_failure.ppm", state.d_next_grid, state.width, state.height);
        return false;
    }

    std::cout << "✅ Тест " << testName << " пройден!" << std::endl;
    return true;
}
