    if (deviceCount == 0) {
        std::cerr << "Нет доступных GPU." << std::endl;
        return false;
    }

    for (int dev = 0; dev < deviceCount; ++dev) {
        cudaSetDevice(dev);
        std::cout << "Запуск на GPU #" << dev << std::endl;

        // Выполняем тесты на каждом GPU
        for (auto& state : states) {
            if (!runRegressionTest("GPU_" + std::to_string(dev), state)) {
                return false;
            }
        }
    }

    return true;
}
