int main() {
    const int WIDTH = 100;
    const int HEIGHT = 100;

    // Инициализация данных (простой паттерн — "пульсирующий блок")
    std::vector<int> initialGrid(WIDTH * HEIGHT, 0);
    for (int i = 40; i < 60; ++i) {
        for (int j = 40; j < 60; ++j) {
            initialGrid[i * WIDTH + j] = 1;
        }
    }

    // Создание состояний для каждого GPU
    std::vector<GridState> states;
    for (int gpuId = 0; gpuId < 2; ++gpuId) { // Два GPU
        GridState state;
        cudaSetDevice(gpuId);

        cudaMalloc(&state.d_grid, WIDTH * HEIGHT * sizeof(int));
        cudaMalloc(&state.d_next_grid, WIDTH * HEIGHT * sizeof(int));

        cudaMemcpy(state.d_grid, initialGrid.data(), WIDTH * HEIGHT * sizeof(int), cudaMemcpyHostToDevice);

        state.width = WIDTH;
        state.height = HEIGHT;
        state.d_grid = state.d_grid; // Убедитесь, что указатель корректный
        state.d_next_grid = state.d_next_grid;

        states.push_back(state);
    }

    // Запуск тестов
    if (runOnMultipleGPUs(states)) {
        std::cout << "✅ Все тесты пройдены успешно!" << std::endl;
    } else {
        std::cout << "❌ Некоторые тесты не прошли." << std::endl;
    }

    // Очистка памяти
    for (auto& state : states) {
        cudaFree(state.d_grid);
        cudaFree(state.d_next_grid);
    }

    return 0;
}
