#pragma once
#include <iostream>
#include <chrono>

struct Logger {
  bool debug = false;
  bool verbose = false;

  void info(const std::string& s) const {
    std::cout << "[INFO] " << s << "\n";
  }
  void warn(const std::string& s) const {
    std::cout << "[WARN] " << s << "\n";
  }
  void error(const std::string& s) const {
    std::cerr << "[ERROR] " << s << "\n";
  }
  void debugf(const char* fmt, ...) const {
    if (!debug) return;
    va_list args;
    va_start(args, fmt);
    vprintf(fmt, args);
    va_end(args);
  }
  void verbosef(const char* fmt, ...) const {
    if (!verbose) return;
    va_list args;
    va_start(args, fmt);
    vprintf(fmt, args);
    va_end(args);
  }

  template <class Func>
  double timing(Func&& f, const std::string& label, bool do_print=true) const {
    auto t0 = std::chrono::high_resolution_clock::now();
    f();
    auto t1 = std::chrono::high_resolution_clock::now();
    double ms = std::chrono::duration<double, std::milli>(t1 - t0).count();
    if (do_print) info(label + " took " + std::to_string(ms) + " ms");
    return ms;
  }
};
