inline void print_device_props(int device) {
  cudaDeviceProp prop{};
  CUDA_CHECK(cudaGetDeviceProperties(&prop, device));
  printf("Device: %s (compute %d.%d, smem %zu KB, const mem %zu KB, total global %zu MB)\n",
         prop.name, prop.major, prop.minor,
         prop.sharedMemPerBlockOptin / 1024,
         prop.totalConstMem / 1024,
         prop.totalGlobalMem / (1024*1024));
}

struct CudaEventTimer {
  cudaEvent_t start{}, stop{};
  CudaEventTimer() {
    CUDA_CHECK(cudaEventCreate(&start));
    CUDA_CHECK(cudaEventCreate(&stop));
  }
  ~CudaEventTimer() {
    cudaEventDestroy(start);
    cudaEventDestroy(stop);
  }
  double record(cudaStream_t stream) {
    CUDA_CHECK(cudaEventRecord(stop, stream));
    CUDA_CHECK(cudaEventSynchronize(stop));
    float ms = 0.f;
    CUDA_CHECK(cudaEventElapsedTime(&ms, start, stop));
    return (double)ms;
  }
};

