src/graph.h
```
#pragma once
#include <vector>
#include <string>
#include <fstream>
#include <cstdint>

// Simple binary format: [header](magic, version, n, k) + [edges] n*k uint32 indices
struct KNNGraph {
  int n = 0;
  int k = 0;
  std::vector<uint32_t> edges; // size n*k, row-major

  void save(const std::string& path) const {
    std::ofstream ofs(path, std::ios::binary);
    if (!ofs) throw std::runtime_error("Cannot open for write: " + path);
    const char magic[8] = {'K','N','N','G','R','A','P','H'};
    uint32_t version = 1;
    ofs.write(magic, sizeof(magic));
    ofs.write((char*)&version, sizeof(version));
    ofs.write((char*)&n, sizeof(n));
    ofs.write((char*)&k, sizeof(k));
    ofs.write((char*)edges.data(), edges.size()*sizeof(uint32_t));
    if (!ofs) throw std::runtime_error("Write failed");
  }

  void load(const std::string& path) {
    std::ifstream ifs(path, std::ios::binary);
    if (!ifs) throw std::runtime_error("Cannot open for read: " + path);
    char magic[8];
    ifs.read(magic, sizeof(magic));
    std::string m(magic, magic+8);
    if (m != "KNNGRAPH") throw std::runtime_error("Bad magic");
    uint32_t version = 0;
    ifs.read((char*)&version, sizeof(version));
    if (version != 1) throw std::runtime_error("Unsupported version");
    ifs.read((char*)&n, sizeof(n));
    ifs.read((char*)&k, sizeof(k));
    edges.resize((size_t)n * (size_t)k);
    ifs.read((char*)edges.data(), edges.size()*sizeof(uint32_t));
    if (!ifs) throw std::runtime_error("Read failed");
  }

  void resize_k(int newk) {
    if (newk <= k) return; // safe: we won't shrink here (caller can sample)
    std::vector<uint32_t> newedges((size_t)n * newk, UINT32_MAX);
    for (int i = 0; i < n; ++i) {
      for (int j = 0; j < k; ++j) {
        newedges[(size_t)i*newk + j] = edges[(size_t)i*k + j];
      }
    }
    edges.swap(newedges);
    k = newk;
  }
};
