#include <string>
#include <vector>

// Dataset is float32 row-major N x D
// Graph is N x k uint32, neighbor indices per row

class KNNGPUWrapper {
public:
  struct Config {
    int k = 32;
    std::string metric = "l2"; // "l2" only
    bool use_gpu = true;
    bool refine_edges = true;
    bool normalize = false;
  };

  KNNGPUWrapper(const Logger& log) : log_(log) {}

  // Build KNN graph from dataset using FAISS on GPU
  void build_graph(const std::vector<float>& data, int N, int D, const Config& cfg);

  // Run graph search queries
  // queries: M x D, returns topk indices and distances
  void search_graph(const std::vector<float>& queries, int M, int topk,
                    int width, int beam, int iters,
                    std::vector<uint32_t>& out_indices,
                    std::vector<float>& out_dists,
                    bool verbose = false);

  // Evaluate recall using brute-force ground-truth on GPU (use FAISS)
  void evaluate(int M, int topk,
                const std::vector<float>& queries,
                const std::vector<uint32_t>& gt_indices,
                const std::vector<float>& gt_dists,
                std::vector<float>& recalls);

  // Save/Load graph
  void save_graph(const std::string& path) const { graph_.save(path); }
  void load_graph(const std::string& path) { graph_.load(path); }

  // Reset graph to top subset by degree (or by sample if degrees are invalid)
  void reset_to_subset(int new_n);

  const KNNGraph& graph() const { return graph_; }
  int n() const { return graph_.n; }
  int k() const { return graph_.k; }
  int dim() const { return dim_; }
  int count() const { return n_; }

private:
  Logger log_;
  int n_ = 0;
  int dim_ = 0;
  KNNGraph graph_;
};
