#include "knn_gpu.h"
#include "cuda_utils.h"

#include <faiss/IndexFlat.h>
#include <faiss/IndexHNSWFlat.h>
#include <faiss/IndexIVFPQ.h>
#include <faiss/Clustering.h>
#include <faiss/gpu/GpuAutoTune.h>
#include <faiss/gpu/GpuClonerOptions.h>
#include <faiss/gpu/GpuResources.h>
#include <faiss/gpu/StandardGpuResources.h>
#include <faiss/gpu/utils/DeviceUtils.h>
#include <faiss/gpu/utils/MathFunctions.h>

#include <algorithm>
#include <cmath>
#include <cstring>
#include <iostream>
#include <random>
#include <set>
#include <unordered_set>

using namespace faiss;

struct DeviceContext {
  int device = 0;
  GpuResources* res = nullptr;
  GpuIndexFlatL2* coarse = nullptr; // L2 index on device
};

static void ensure_device() {
  int ndev = 0;
  CUDA_CHECK(cudaGetDeviceCount(&ndev));
  if (ndev == 0) throw std::runtime_error("No CUDA devices found");
  print_device_props(0);
  CUDA_CHECK(cudaSetDevice(0));
}

static GpuResources* create_gpu_res() {
  // Use standard resources with pinned memory
  return new StandardGpuResources();
}

static std::vector<int> argsort_degrees(const std::vector<uint32_t>& degrees) {
  std::vector<int> idx(degrees.size());
  std::iota(idx.begin(), idx.end(), 0);
  std::stable_sort(idx.begin(), idx.end(), [&](int a, int b) {
    return degrees[a] < degrees[b];
  });
  return idx;
}

static std::vector<uint32_t> compute_degrees(const KNNGraph& g) {
  std::vector<uint32_t> deg(g.n, 0);
  for (int i = 0; i < g.n; ++i) {
    for (int j = 0; j < g.k; ++j) {
      uint32_t nb = g.edges[(size_t)i * g.k + j];
      if (nb < (uint32_t)g.n) deg[nb]++;
    }
  }
  return deg;
}

static float sqr_l2(const float* a, const float* b, int d) {
  float acc = 0.f;
  for (int i = 0; i < d; ++i) {
    float diff = a[i] - b[i];
    acc += diff * diff;
  }
  return acc;
}

void KNNGPUWrapper::build_graph(const std::vector<float>& data, int N, int D, const Config& cfg) {
  ensure_device();
  log_.info("Building KNN graph (GPU) N=" + std::to_string(N) + " D=" + std::to_string(D) + " k=" + std::to_string(cfg.k));
  n_ = N; dim_ = D;
  if (cfg.normalize) {
    log_.info("Normalizing vectors (L2) on CPU");
    std::vector<float> tmp(N*D);
    for (int i = 0; i < N; ++i) {
      float* row = tmp.data() + (size_t)i*D;
      std::memcpy(row, data.data() + (size_t)i*D, D*sizeof(float));
      float norm = std::sqrt(sqr_l2(row, row, D));
      if (norm > 0.f) {
        for (int j = 0; j < D; ++j) row[j] /= norm;
      }
    }
    const_cast<std::vector<float>&>(data) = std::move(tmp);
  }

  // Use GPU resources and flat L2 index
  DeviceContext ctx;
  ctx.device = 0;
  ctx.res = create_gpu_res();

  IndexFlatL2 cpu_index(D);
  // If dataset is very large, we might consider coarse quantization or HNSW to reduce cost.
  // For simplicity and quality, we use exhaustive search. For N>2e6 adjust strategy.
  if (N > 2000000) {
    // Coarse quantizer for ivf; nlist heuristic
    int nlist = std::min<int>(int(std::sqrt(N) * 4), 65536);
    GpuIndexIVFPQ ivf(ctx.res, D, nlist, 64, 8); // 64 centroids * 8 sub-vectors
    // train
    ClusteringParameters cp;
    cp.niter = 40;
    Clustering clu(D, nlist, cp);
    std::vector<float> x(N * D);
    std::memcpy(x.data(), data.data(), x.size()*sizeof(float));
    Index::ClusteringToPracticeParameters cparam;
    Clustering clu2 = clu;
    clu2.train(N, x.data(), cpu_index); // trains on CPU; it's fine
    // set trained quantizer
    ivf.nlist = nlist;
    ivf.quantizer = new IndexFlatL2(D);
    ((IndexFlatL2*)ivf.quantizer)->is_trained = true;
    ((IndexFlatL2*)ivf.quantizer)->ntotal = nlist;
    // copy centroids
    std::memcpy(((IndexFlatL2*)ivf.quantizer)->xb.data(), clu.centroids.data(), clu.centroids.size()*sizeof(float));

    // train PQ on subset
    int64_t ntrain = std::min<int64_t>(N, 200000);
    ivf.train(ntrain, data.data());
    // add all
    ivf.add(N, data.data());
    // search to get k
    std::vector<float> dists((size_t)N * cfg.k);
    std::vector<idx_t> ids((size_t)N * cfg.k);
    ivf.search(N, data.data(), cfg.k, dists.data(), ids.data());

    graph_.n = N; graph_.k = cfg.k; graph_.edges.resize((size_t)N * cfg.k);
    for (size_t i = 0; i < (size_t)N * cfg.k; ++i) {
      graph_.edges[i] = (uint32_t)ids[i];
    }
    log_.info("Built initial graph using IVF-PQ");
  } else {
    GpuIndexFlatL2 gpu_index(ctx.res, D, false, nullptr);
    gpu_index.add(N, data.data());
    std::vector<float> dists((size_t)N * cfg.k);
    std::vector<idx_t> ids((size_t)N * cfg.k);
    gpu_index.search(N, data.data(), cfg.k, dists.data(), ids.data());
    graph_.n = N; graph_.k = cfg.k; graph_.edges.resize((size_t)N * cfg.k);
    for (size_t i = 0; i < (size_t)N * cfg.k; ++i) {
      graph_.edges[i] = (uint32_t)ids[i];
    }
    log_.info("Built initial graph using exact flat (GPU)");
  }

  if (cfg.refine_edges) {
    log_.info("Refining edges with edge exchange (1-pass GPU)");
    // 1-pass refinement using local candidates from neighbors-of-neighbors, dedup + resort
    int passes = 1;
    std::vector<std::unordered_set<uint32_t>> nbset((size_t)graph_.n);
    for (int pass = 0; pass < passes; ++pass) {
      nbset.clear();
      nbset.resize((size_t)graph_.n);
      // collect candidate sets
      for (int i = 0; i < graph_.n; ++i) {
        for (int j = 0; j < graph_.k; ++j) {
          uint32_t nb = graph_.edges[(size_t)i * graph_.k + j];
          nbset[i].insert(nb);
        }
        // also neighbors-of-neighbors
        for (int j = 0; j < graph_.k; ++j) {
          uint32_t nb = graph_.edges[(size_t)i * graph_.k + j];
          if (nb >= (uint32_t)graph_.n) continue;
          for (int t = 0; t < graph_.k; ++t) {
            uint32_t nb2 = graph_.edges[(size_t)nb * graph_.k + t];
            nbset[i].insert(nb2);
          }
        }
        // optional self
        nbset[i].insert(i);
      }
      // recompute top-k for each i using CPU distances (tradeoff simplicity; dataset must fit in RAM)
      // For larger scale, move this to GPU; this is a demonstration.
      std::vector<float> cand_dists;
      std::vector<uint32_t> cand_ids;
      std::vector<uint32_t> new_edges((size_t)graph_.n * graph_.k, UINT32_MAX);

      for (int i = 0; i < graph_.n; ++i) {
        cand_ids.assign(nbset[i].begin(), nbset[i].end());
        size_t cand_n = cand_ids.size();
        cand_dists.resize(cand_n);
        const float* vi = data.data() + (size_t)i * D;
        for (size_t c = 0; c < cand_n; ++c) {
          uint32_t idx = cand_ids[c];
          const float* vj = data.data() + (size_t)idx * D;
          cand_dists[c] = sqr_l2(vi, vj, D);
        }
        // partial sort top-k
        std::vector<size_t> idx(cand_n);
        std::iota(idx.begin(), idx.end(), 0);
        std::nth_element(idx.begin(), idx.begin() + std::min<size_t>(graph_.k, cand_n), idx.end(),
          [&](size_t a, size_t b) { return cand_dists[a] < cand_dists[b]; });
        size_t top = std::min<size_t>(graph_.k, cand_n);
        std::partial_sort(idx.begin(), idx.begin() + top, idx.end(),
          [&](size_t a, size_t b) { return cand_dists[a] < cand_dists[b]; });
        for (size_t t = 0; t < top; ++t) {
          new_edges[(size_t)i * graph_.k + t] = cand_ids[idx[t]];
        }
      }
      graph_.edges.swap(new_edges);
      log_.info("Refine pass " + std::to_string(pass+1) + " done");
    }
  }
}

void KNNGPUWrapper::reset_to_subset(int new_n) {
  if (new_n >= graph_.n) {
    log_.warn("Requested subset size >= current size, no-op");
    return;
  }
  log_.info("Resetting graph to top " + std::to_string(new_n) + " nodes by degree");
  auto deg = compute_degrees(graph_);
  auto order = argsort_degrees(deg);
  std::vector<uint32_t> old_to_new(graph_.n, UINT32_MAX);
  for (int i = 0; i < new_n; ++i) {
    old_to_new[order[graph_.n - 1 - i]] = i;
  }
  KNNGraph ng;
  ng.n = new_n; ng.k = graph_.k; ng.edges.assign((size_t)new_n * ng.k, UINT32_MAX);
  for (int old_i = 0; old_i < graph_.n; ++old_i) {
    int new_i = old_to_new[old_i];
    if (new_i == -1) continue;
    for (int j = 0; j < graph_.k; ++j) {
      uint32_t nb_old = graph_.edges[(size_t)old_i * graph_.k + j];
      uint32_t nb_new = old_to_new[nb_old];
      if (nb_new != UINT32_MAX) {
        ng.edges[(size_t)new_i * ng.k + j] = nb_new;
      }
    }
  }
  graph_ = std::move(ng);
}

static void greedy_walk_search(
  int N, int D, int topk, int width, int beam, int iters,
  const std::vector<float>& data,
  const std::vector<float>& queries,
  const KNNGraph& g,
  std::vector<uint32_t>& out_indices,
  std::vector<float>& out_dists,
  const Logger& log)
{
  // data: N x D row-major
  // queries: M x D
  int M = (int)queries.size() / D;
  out_indices.assign((size_t)M * topk, UINT32_MAX);
  out_dists.assign((size_t)M * topk, 1e30f);

  std::vector<float> best_dists; best_dists.resize((size_t)M * width);
  std::vector<uint32_t> best_ids; best_ids.resize((size_t)M * width);
  std::vector<int> frontier(M);
  std::vector<char> seen; seen.resize((size_t)M * N); // could be big; this is illustrative
  std::fill(seen.begin(), seen.end(), 0);

  // init frontier as nearest among random nodes
  std::mt19937 rng(123);
  for (int m = 0; m < M; ++m) {
    frontier[m] = rng() % N;
    seen[(size_t)m * N + frontier[m]] = 1;
  }
  // compute initial distances
  for (int m = 0; m < M; ++m) {
    const float* q = queries.data() + (size_t)m * D;
    for (int w = 0; w < width; ++w) {
      const float* v = data.data() + (size_t)frontier[m] * D;
      best_dists[(size_t)m * width + w] = sqr_l2(q, v, D);
      best_ids[(size_t)m * width + w] = frontier[m];
    }
  }

  // iterative expansion
  for (int it = 0; it < iters; ++it) {
    // pick best per-query (beam)
    std::vector<int> beam_ids(M);
    for (int m = 0; m < M; ++m) {
      int best = 0;
      float bestd = best_dists[(size_t)m * width + 0];
      for (int w = 1; w < width; ++w) {
        float d = best_dists[(size_t)m * width + w];
        if (d < bestd) { bestd = d; best = w; }
      }
      beam_ids[m] = best_ids[(size_t)m * width + best];
      best_dists[(size_t)m * width + 0] = bestd; // keep the best in slot 0
      best_ids[(size_t)m * width + 0] = beam_ids[m];
    }

    // expand neighbors
    for (int m = 0; m < M; ++m) {
      int u = beam_ids[m];
      // fetch neighbors of u
      for (int j = 0; j < g.k; ++j) {
        uint32_t v = g.edges[(size_t)u * g.k + j];
        if (v >= (uint32_t)N || seen[(size_t)m * N + v]) continue;
        seen[(size_t)m * N + v] = 1;
        const float* q = queries.data() + (size_t)m * D;
        const float* vec = data.data() + (size_t)v * D;
        float d = sqr_l2(q, vec, D);
        // insert into candidates array (width), we keep a small linear scan
        int worst = 0;
        float worstd = best_dists[(size_t)m * width + 0];
        for (int w = 1; w < width; ++w) {
          if (best_dists[(size_t)m * width + w] > worstd) {
            worstd = best_dists[(size_t)m * width + w];
            worst = w;
          }
        }
        if (d < worstd) {
          best_dists[(size_t)m * width + worst] = d;
          best_ids[(size_t)m * width + worst] = v;
        }
      }
    }

    // update global topk with candidates
    for (int m = 0; m < M; ++m) {
      for (int w = 0; w < width; ++w) {
        float d = best_dists[(size_t)m * width + w];
        uint32_t id = best_ids[(size_t)m * width + w];
        // insert into topk if better than current worst
        float worst = -1.f;
        int worstp = -1;
        for (int t = 0; t < topk; ++t) {
          float dt = out_dists[(size_t)m * topk + t];
          if (dt < 1e29f) { // valid
            if (dt > worst) { worst = dt; worstp = t; }
          } else {
            worst = dt; worstp = t; break;
          }
        }
        if (worstp >= 0 && d < worst) {
          out_dists[(size_t)m * topk + worstp] = d;
          out_indices[(size_t)m * topk + worstp] = id;
        }
      }
      // sort topk to keep ascending order
      for (int a = 1; a < topk; ++a) {
        for (int b = a; b > 0; --b) {
          float d1 = out_dists[(size_t)m * topk + b];
          float d0 = out_dists[(size_t)m * topk + b-1];
          if (d1 < d0) {
            std::swap(out_dists[(size_t)m * topk + b], out_dists[(size_t)m * topk + b-1]);
            std::swap(out_indices[(size_t)m * topk + b], out_indices[(size_t)m * topk + b-1]);
          }
        }
      }
    }
    if (iters > 1) log.debugf("Iter %d done\n", it+1);
  }
}

void KNNGPUWrapper::search_graph(const std::vector<float>& queries, int M, int topk,
                                 int width, int beam, int iters,
                                 std::vector<uint32_t>& out_indices,
                                 std::vector<float>& out_dists,
                                 bool verbose) {
  if (graph_.n == 0) throw std::runtime_error("Graph not built or loaded");
  if (verbose) {
    log_.info("Search on graph: M=" + std::to_string(M) + " topk=" + std::to_string(topk)
      + " width=" + std::to_string(width) + " beam=" + std::to_string(beam) + " iters=" + std::to_string(iters));
  }
  // For simplicity, we use greedy graph walk search on CPU with dataset in RAM.
  // For large scale/high throughput, move this to GPU with shared memory caches and batched kernels.
  greedy_walk_search(graph_.n, dim_, topk, width, beam, iters, // treat width as beam for simplicity
    getExternalData(), queries, graph_, out_indices, out_dists, log_);
}

void KNNGPUWrapper::evaluate(int M, int topk,
                             const std::vector<float>& queries,
                             const std::vector<uint32_t>& gt_indices,
                             const std::vector<float>& gt_dists,
                             std::vector<float>& recalls) {
  // Simple CPU-side recall: for each query, check how many predicted indices appear in GT top-k
  // gt_indices size M*topk
  if ((int)gt_indices.size() != M*topk) {
    throw std::runtime_error("GT size mismatch");
  }
  recalls.assign(M, 0.f);
  // out_indices/out_dists should be set by caller after search_graph
  if (out_indices.empty()) throw std::runtime_error("Call search_graph first");

  for (int m = 0; m < M; ++m) {
    std::unordered_set<uint32_t> gt_set;
    gt_set.reserve(topk*2);
    for (int t = 0; t < topk; ++t) {
      gt_set.insert(gt_indices[(size_t)m * topk + t]);
    }
    int hit = 0;
    for (int t = 0; t < topk; ++t) {
      uint32_t id = out_indices[(size_t)m * topk + t];
      if (gt_set.find(id) != gt_set.end()) hit++;
    }
    recalls[m] = hit / (float)topk;
  }
}
