#include "knn_gpu.h"
#include "logger.h"
#include "cuda_utils.h"
#include <args.hxx>
#include <iostream>
#include <fstream>
#include <vector>
#include <chrono>

using args::Parser;

static bool endswith(const std::string& s, const std::string& suf) {
  if (s.size() < suf.size()) return false;
  return s.compare(s.size()-suf.size(), suf.size(), suf) == 0;
}

static void read_bin_f32(const std::string& path, std::vector<float>& x, int& N, int& D) {
  std::ifstream ifs(path, std::ios::binary);
  if (!ifs) throw std::runtime_error("Cannot open " + path);
  ifs.read((char*)&N, sizeof(N));
  ifs.read((char*)&D, sizeof(D));
  x.resize((size_t)N * D);
  ifs.read((char*)x.data(), x.size()*sizeof(float));
  if (!ifs) throw std::runtime_error("Read failed");
}

int main(int argc, char** argv) {
  Parser p("GPU KNN Wrapper");
  p.Add("--dataset", "Dataset file (float32 NxD .fbin)", args::type<std::string>{});
  p.Add("--queries", "Queries file (float32 MxD .fbin)", args::type<std::string>{});
  p.Add("--graph-out", "Graph output file", args::type<std::string>{});
  p.Add("--graph-in", "Graph input file", args::type<std::string>{});
  p.Add("--k", "k neighbors (build)", args::type<int>{}, 32);
  p.Add("--topk", "topk (query/eval)", args::type<int>{}, 10);
  p.Add("--metric", "distance metric (only l2)", args::type<std::string>{}, "l2");
  p.Add("--refine", "refine edges after build (0/1)", args::type<bool>{}, true);
  p.Add("--mode", "build|query|eval", args::type<std::string>{});
  p.Add("--search-width", "search width (candidate pool)", args::type<int>{}, 64);
  p.Add("--beam", "beam size", args::type<int>{}, 32);
  p.Add("--iters", "search iterations", args::type<int>{}, 3);
  p.Add("--normalize", "normalize dataset before build", args::type<bool>{}, false);
  p.Add("--subset", "reset graph to subset of size N", args::type<int>{}, -1);
  p.Add("--verbose", "verbose output", args::type<bool>{}, false);
  p.Add("--debug", "debug output", args::type<bool>{}, false);

  if (!p.Parse(argc, argv)) {
    std::cerr << "Args error\n";
    return 1;
  }
  Logger log;
  log.verbose = args::get(p("--verbose"));
  log.debug = args::get(p("--debug"));

  std::string dataset_path = args::get(p("--dataset"));
  std::string queries_path = args::get(p("--queries"));
  std::string graph_out = args::get(p("--graph-out"));
  std::string graph_in = args::get(p("--graph-in"));
  std::string mode = args::get(p("--mode"));
  int k = args::get(p("--k"));
  int topk = args::get(p("--topk"));
  int width = args::get(p("--search-width"));
  int beam = args::get(p("--beam"));
  int iters = args::get(p("--iters"));
  bool refine = args::get(p("--refine"));
  bool normalize = args::get(p("--normalize"));
  int subset_n = args::get(p("--subset"));

  KNNGPUWrapper wrapper(log);

  try {
    if (mode == "build") {
      std::vector<float> data; int N, D;
      read_bin_f32(dataset_path, data, N, D);
      KNNGPUWrapper::Config cfg;
      cfg.k = k;
      cfg.refine_edges = refine;
      cfg.normalize = normalize;
      auto ms = log.timing([&](){ wrapper.build_graph(data, N, D, cfg); }, "build_graph");
      if (!graph_out.empty()) wrapper.save_graph(graph_out);
      log.info("Graph built: N=" + std::to_string(wrapper.n()) + " k=" + std::to_string(wrapper.k()));
    } else if (mode == "query") {
      if (graph_in.empty()) throw std::runtime_error("Provide --graph-in for query");
      wrapper.load_graph(graph_in);
      std::vector<float> q; int M, D;
      read_bin_f32(queries_path, q, M, D);
      if (D != wrapper.dim()) {
        log.warn("Query dim differs from dataset dim (" + std::to_string(D) + " vs " + std::to_string(wrapper.dim()) + ")");
      }
      std::vector<uint32_t> indices;
      std::vector<float> dists;
      log.timing([&](){
        wrapper.search_graph(q, M, topk, width, beam, iters, indices, dists, log.verbose);
      }, "graph search", true);
      // output first query topk
      std::cout << "Sample results for first query (top " << topk << "):\n";
      for (int t = 0; t < topk; ++t) {
        std::cout << t << ": id=" << indices[t] << " dist=" << dists[t] << "\n";
      }
    } else if (mode == "eval") {
      if (graph_in.empty() || dataset_path.empty() || queries_path.empty())
        throw std::runtime_error("For eval, provide --graph-in, --dataset, --queries");
      wrapper.load_graph(graph_in);
      std::vector<float> data; int N, D;
      read_bin_f32(dataset_path, data, N, D);
      std::vector<float> q; int M, Qd;
      read_bin_f32(queries_path, q, M, Qd);
      if (D != Qd) throw std::runtime_error("Dimension mismatch");
      // Build GT using FAISS GPU flat for fair baseline
      faiss::gpu::StandardGpuResources res;
      faiss::gpu::GpuIndexFlatL2 gpu_index(&res, D, false, nullptr);
      gpu_index.add(N, data.data());
      std::vector<faiss::idx_t> gt_ids((size_t)M * topk);
      std::vector<float> gt_dists((size_t)M * topk);
      gpu_index.search(M, q.data(), topk, gt_dists.data(), gt_ids.data());

      // Run our search
      std::vector<uint32_t> indices;
      std::vector<float> dists;
      wrapper.search_graph(q, M, topk, width, beam, iters, indices, dists, log.verbose);

      // Compute recall
      std::vector<float> recalls;
      // wrapper.evaluate(M, topk, q, ... ) expects that indices/dists are from wrapper after search_graph
      wrapper.evaluate(M, topk, q, reinterpret_cast<const std::vector<uint32_t>&>(gt_ids), gt_dists, recalls);
      float mean = 0.f;
      for (float r : recalls) mean += r;
      mean /= std::max(1, M);
      std::cout << "Mean recall@" << topk << " = " << mean << "\n";
    } else {
      throw std::runtime_error("Unknown mode: " + mode);
    }

    if (subset_n > 0) {
      if (graph_in.empty() && graph_out.empty())
        throw std::runtime_error("Provide graph (--graph-in or build) before reset");
      if (!graph_out.empty()) {
        // if we built previously in this run, graph_ is in memory; else we need to load
      }
      wrapper.reset_to_subset(subset_n);
      if (!graph_out.empty()) wrapper.save_graph(graph_out);
    }

  } catch (const std::exception& e) {
    log.error(std::string("Exception: ") + e.what());
    return 1;
  }
  return 0;
}
