build.mjs
```
import path from "node:path";
import fs from "fs-extra";
import fg from "fast-glob";
import ejs from "ejs";
import YAML from "yaml";
import { fileURLToPath } from "node:url";
import { execSync } from "node:child_process";
import esbuild from "esbuild";
import postcss from "postcss";
import autoprefixer from "autoprefixer";

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const CONFIG_PATH = path.join(__dirname, "site.config.yaml");
const config = YAML.parse(await fs.readFile(CONFIG_PATH, "utf8"));

const SRC = {
  layouts: config.templates.layoutsDir,
  pages: config.templates.pagesDir,
  data: config.templates.dataDir,
  grammars: config.parsers.srcDir,
  styles: config.styles.srcDir,
  scripts: config.scripts.srcDir
};

const OUT = {
  base: "public",
  css: config.styles.outDir,
  js: config.scripts.outDir,
  parsers: config.parsers.outDir,
  assets: "public/assets",
  data: path.join("public", "data")
};

function log(msg) {
  console.log(`[build] ${msg}`);
}

async function ensureDirs(dirList = []) {
  await Promise.all(dirList.map((d) => fs.ensureDir(d)));
  log(`Ensured directories: ${dirList.join(", ")}`);
}

async function createPublicStructure() {
  const dirs = new Set([...config.public.dirs, OUT.css, OUT.js, OUT.parsers, OUT.assets, OUT.data]);
  await ensureDirs([...dirs]);

  // создаём указанные файлы
  for (const f of config.public.files) {
    const abs = path.join(OUT.base, f.path);
    await fs.ensureDir(path.dirname(abs));
    if (f.content !== undefined) {
      if (f.path.endsWith("/")) {
        await fs.ensureDir(abs);
      } else {
        await fs.writeFile(abs, f.content, "utf8");
      }
    } else {
      await fs.ensureFile(abs);
    }
  }
  log("Public structure created");
}

async function generateParsers() {
  if (!(await fs.pathExists(SRC.grammars))) {
    log(`Parsers src dir not found: ${SRC.grammars}. Skipping.`);
    return;
  }
  const files = await fg(`${SRC.grammars}/**/*.jison`);
  if (!files.length) {
    log("No .jison files found. Skipping parser generation.");
    return;
  }

  await fs.ensureDir(OUT.parsers);
  const tmpl = `
/* eslint-disable */
// This file is auto-generated from {{filename}} by build.mjs
import parser from "./{{basename}}.js";
export default parser;
`;

  for (const file of files) {
    const cmd = `jison "${file}" -o "${OUT.parsers}" --module-type ${config.parsers.moduleType || "esm"}`;
    log(`Generating parser: ${path.relative(".", file)}`);
    execSync(cmd, { stdio: "inherit" });

    const basename = path.basename(file).replace(/\.jison$/, "");
    const outJs = path.join(OUT.parsers, `${basename}.js`);
    if (config.parsers.moduleType === "esm") {
      // добавим export default parser для удобного импорта
      const wrapper = ejs.render(tmpl, { filename: path.relative(__dirname, file), basename });
      await fs.appendFile(outJs, `\n${wrapper}\n`);
    }
  }
  log("Parsers generated");
}

async function processStyles() {
  if (!(await fs.pathExists(SRC.styles))) {
    log(`Styles src dir not found: ${SRC.styles}. Skipping.`);
    return;
  }
  const files = await fg([`${SRC.styles}/**/*.css`, `!${SRC.styles}/**/*.min.css`]);
  if (!files.length) {
    log("No CSS files found. Skipping style processing.");
    return;
  }

  await fs.ensureDir(OUT.css);
  for (const file of files) {
    const rel = path.relative(SRC.styles, file);
    const outFile = path.join(OUT.css, rel);
    await fs.ensureDir(path.dirname(outFile));
    const css = await fs.readFile(file, "utf8");

    if (config.styles.postcss) {
      const result = await postcss([autoprefixer()]).process(css, { from: file });
      await fs.writeFile(outFile, result.css, "utf8");
    } else {
      await fs.writeFile(outFile, css, "utf8");
    }
    log(`CSS built: ${rel}`);
  }
}

async function processScripts() {
  if (!(await fs.pathExists(SRC.scripts))) {
    log(`Scripts src dir not found: ${SRC.scripts}. Skipping.`);
    return;
  }
  const files = await fg([`${SRC.scripts}/**/*.js`, `!${SRC.scripts}/**/*.min.js`]);
  if (!files.length) {
    log("No JS files found. Skipping script bundling.");
    return;
  }

  await fs.ensureDir(OUT.js);
  const entry = {};
  for (const file of files) {
    const name = path.relative(SRC.scripts, file).replace(/\\/g, "/").replace(/\.js$/, "");
    entry[name] = file;
  }

  const result = await esbuild.build({
    entryPoints: entry,
    outdir: OUT.js,
    bundle: false,
    sourcemap: false,
    format: config.scripts.format || "esm",
    target: config.scripts.target || "es2018",
    logLevel: "silent",
    loader: { ".ts": "ts", ".jsx": "jsx", ".json": "json" }
  });

  if (result.errors?.length) {
    console.error(result.errors);
    throw new Error("esbuild failed");
  }
  log("Scripts bundled");
}

function parseFrontMatter(content) {
  const re = /^---\r?\n([\s\S]*?)\r?\n---\r?\n?/;
  const m = content.match(re);
  if (!m) return { meta: {}, body: content };
  const meta = YAML.parse(m[1].trim());
  const body = content.slice(m[0].length);
  return { meta, body };
}

async function renderTemplate(templatePath, data) {
  const tpl = await fs.readFile(templatePath, "utf8");
  return ejs.render(tpl, data, { filename: templatePath });
}

async function processPages() {
  const layoutsDir = SRC.layouts;
  const pagesDir = SRC.pages;
  const dataDir = SRC.data;

  if (!(await fs.pathExists(layoutsDir))) {
    log(`Layouts dir not found: ${layoutsDir}. Skipping pages.`);
    return;
  }
  if (!(await fs.pathExists(pagesDir))) {
    log(`Pages dir not found: ${pagesDir}. Skipping pages.`);
    return;
  }

  const files = await fg([`${pagesDir}/**/*.*`, `!${pagesDir}/**/_*.*`]);
  if (!files.length) {
    log("No page files found. Skipping pages.");
    return;
  }

  // Подготовка общих данных для шаблонов
  const site = config.site || {};
  let siteData = {};
  if (await fs.pathExists(dataDir)) {
    const dataFiles = await fg([`${dataDir}/**/*.{json,yaml,yml}`]);
    for (const f of dataFiles) {
      const key = path.basename(f).replace(/\.(json|ya?ml)$/i, "");
      const ext = path.extname(f).toLowerCase();
      const raw = await fs.readFile(f, "utf8");
      siteData[key] = ext === ".json" ? JSON.parse(raw) : YAML.parse(raw);
    }
  }

  // Карта шаблонов по имени
  const layoutFiles = await fg([`${layoutsDir}/**/*.ejs`]);
  const layoutsByName = {};
  for (const lf of layoutFiles) {
    const name = path.basename(lf).replace(/\.ejs$/, "");
    layoutsByName[name] = lf;
  }

  for (const file of files) {
    const rel = path.relative(pagesDir, file);
    const ext = path.extname(file).toLowerCase();
    let outRel = rel;
    if (ext === ".md" || ext === ".ejs") {
      outRel = rel.replace(/\.(md|ejs)$/i, ".html");
    } else {
      // для остальных расширений просто копируем в public
      const dest = path.join(OUT.base, rel);
      await fs.ensureDir(path.dirname(dest));
      await fs.copyFile(file, dest);
      log(`Asset copied: ${rel}`);
      continue;
    }

    const raw = await fs.readFile(file, "utf8");
    const { meta, body } = parseFrontMatter(raw);

    // Данные для шаблона
    const pageData = {
      site,
      page: meta,
      content: body,
      data: siteData,
      url: "/" + outRel.replace(/\\/g, "/")
    };

    const layoutName = meta.layout || "base";
    const layoutPath = layoutsByName[layoutName];
    if (!layoutPath) {
      throw new Error(`Layout "${layoutName}.ejs" not found for ${rel}`);
    }

    const html = await renderTemplate(layoutPath, pageData);
    const outFile = path.join(OUT.base, outRel);
    await fs.ensureDir(path.dirname(outFile));
    await fs.writeFile(outFile, html, "utf8");
    log(`Page rendered: ${rel} -> ${outRel}`);
  }
}

async function copyAssets() {
  if (!(await fs.pathExists("src/assets"))) {
    log("No src/assets directory found. Skipping assets.");
    return;
  }
  const entries = config.assets || [{ src: "src/assets/**", dest: "public/assets" }];
  for (const entry of entries) {
    const srcAbs = path.resolve(entry.src);
    const destAbs = path.resolve(entry.dest);
    await fs.ensureDir(destAbs);
    await fs.copy(srcAbs, destAbs, { overwrite: true, errorOnExist: false });
  }
  log("Assets copied");
}

async function main() {
  await fs.emptyDir(OUT.base);
  await createPublicStructure();
  await generateParsers();
  await processStyles();
  await processScripts();
  await processPages();
  await copyAssets();
  log("Build complete");
}

main().catch((err) => {
  console.error(err);
  process.exit(1);
});
