    // LiveData для хранения списка постов
    private val _posts = MutableLiveData<List<Post>>()
    val posts: LiveData<List<Post>> get() = _posts

    // Корутина для асинхронного получения данных
    private val viewModelJob = Job()
    private val uiScope = CoroutineScope(Dispatchers.Main + viewModelJob)

    init {
        fetchPosts()
    }

    // Метод для получения постов из репозитория
    private fun fetchPosts() {
        uiScope.launch {
            try {
                val result = withContext(Dispatchers.IO) {
                    repository.getPosts()
                }
                _posts.value = result
            } catch (e: Exception) {
                // Обработка ошибок, например, установка значения в null или вывод сообщения об ошибке
                _posts.value = null
                // Здесь можно добавить логирование или уведомление пользователя
            }
        }
    }

    // Метод для обновления списка постов
    fun refreshPosts() {
        fetchPosts()
    }

    override fun onCleared() {
        super.onCleared()
        viewModelJob.cancel()
    }
}

// PostRepository.kt
interface PostRepository {
    suspend fun getPosts(): List<Post>
}

// Post.kt
data class Post(
    val id: Int,
    val title: String,
    val body: String
)
```

