// Ядро для обновления состояния клеток
__global__ void gameOfLifeKernel(const int* __restrict__ currentGrid, int* __restrict__ nextGrid, int width, int height) {
    int x = blockIdx.x * blockDim.x + threadIdx.x;
    int y = blockIdx.y * blockDim.y + threadIdx.y;

    if (x >= width || y >= height) return;

    int idx = y * width + x;
    int liveNeighbors = 0;

    // Соседние клетки
    for (int dy = -1; dy <= 1; ++dy) {
        for (int dx = -1; dx <= 1; ++dx) {
            if (dx == 0 && dy == 0) continue;
            int nx = x + dx;
            int ny = y + dy;

            if (nx >= 0 && nx < width && ny >= 0 && ny < height) {
                liveNeighbors += currentGrid[ny * width + nx];
            }
        }
    }

    // Применение правил Игры Жизни
    if (currentGrid[idx] == 1) {
        if (liveNeighbors < 2 || liveNeighbors > 3) {
            nextGrid[idx] = 0;
        } else {
            nextGrid[idx] = 1;
        }
    } else {
        if (liveNeighbors == 3) {
            nextGrid[idx] = 1;
        } else {
            nextGrid[idx] = 0;
        }
    }
}

// Функция для обновления сетки на одном GPU
void updateGridOnGPU(int* d_currentGrid, int* d_nextGrid, int width, int height, cudaStream_t stream) {
    dim3 blockSize(16, 16);
    dim3 gridSize((width + blockSize.x - 1) / blockSize.x, (height + blockSize.y - 1) / blockSize.y);

    gameOfLifeKernel<<<gridSize, blockSize, 0, stream>>>(d_currentGrid, d_nextGrid, width, height);
}

// Функция для обмена граничными данными между GPU
