// Функция для выполнения симуляции
void runSimulation(int* h_initialGrid, int width, int height, int numIterations, int numGPUs) {
    // Выделение памяти на каждом GPU
    std::vector<int*> d_currentGrids(numGPUs);
    std::vector<int*> d_nextGrids(numGPUs);
    std::vector<cudaStream_t> streams(numGPUs);

    for (int i = 0; i < numGPUs; ++i) {
        cudaSetDevice(i);
        CUDA_CHECK_ERROR(cudaMalloc(&d_currentGrids[i], width * height * sizeof(int)));
        CUDA_CHECK_ERROR(cudaMalloc(&d_nextGrids[i], width * height * sizeof(int)));
        CUDA_CHECK_ERROR(cudaMemcpy(d_currentGrids[i], h_initialGrid, width * height * sizeof(int), cudaMemcpyHostToDevice));
        CUDA_CHECK_ERROR(cudaStreamCreate(&streams[i]));
    }

    for (int iter = 0; iter < numIterations; ++iter) {
        for (int i = 0; i < numGPUs; ++i) {
            cudaSetDevice(i);
            updateGridOnGPU(d_currentGrids[i], d_nextGrids[i], width, height, streams[i]);
        }

        // Обмен граничными данными между GPU
        exchangeBoundaries(d_currentGrids.data(), numGPUs, width, height, streams.data());

        // Копирование данных обратно на хост для проверки
        for (int i = 0; i < numGPUs; ++i) {
            cudaSetDevice(i);
            CUDA_CHECK_ERROR(cudaMemcpyAsync(h_initialGrid, d_nextGrids[i], width * height * sizeof(int), cudaMemcpyDeviceToHost, streams[i]));
