        // Здесь должна быть реализация регрессионных тестов
    }

    // Освобождение ресурсов
    for (int i = 0; i < numGPUs; ++i) {
        cudaSetDevice(i);
        CUDA_CHECK_ERROR(cudaFree(d_currentGrids[i]));
        CUDA_CHECK_ERROR(cudaFree(d_nextGrids[i]));
