    // Генерация эталонных данных (например, точные KNN)
    void generate_ground_truth();

    // Оценка графа (точность, recall и т.д.)
    void evaluate_graph();

    // Запросы
    std::vector<IndexType> query(IndexType query_idx, IndexType k_query = -1);
    std::vector<std::vector<IndexType>> batch_query(const std::vector<IndexType>& query_indices, IndexType k_query = -1);

