#include "knn_graph.hpp"
#include "utils.cpp"

KNNGraphWrapper::KNNGraphWrapper(IndexType k, IndexType n, bool debug)
    : k(k), n(n), debug(debug) {
    allocate_cuda_memory();
}

KNNGraphWrapper::~KNNGraphWrapper() {
    free_cuda_memory();
}

void KNNGraphWrapper::allocate_cuda_memory() {
    cudaMalloc(&d_data, n * data[0].size() * sizeof(DataType));
    cudaMalloc(&d_indices, n * k * sizeof(IndexType));
    cudaMalloc(&d_distances, n * k * sizeof(DistanceType));
    cudaMalloc(&d_knn_indices, n * k * sizeof(IndexType));
    cudaMalloc(&d_knn_distances, n * k * sizeof(DistanceType));
}

void KNNGraphWrapper::free_cuda_memory() {
    cudaFree(d_data);
    cudaFree(d_indices);
    cudaFree(d_distances);
    cudaFree(d_knn_indices);
    cudaFree(d_knn_distances);
}

bool KNNGraphWrapper::load_data(const std::string& filename, const std::string& format) {
    if (format == "binary") {
        std::ifstream file(filename, std::ios::binary);
        if (!file.is_open()) {
            if (debug) std::cerr << "Ошибка: не удалось открыть файл " << filename << std::endl;
            return false;
        }
        // Пример: считываем n, d, затем данные
        IndexType d;
        file.read(reinterpret_cast<char*>(&d), sizeof(IndexType));
        data.resize(n, std::vector<DataType>(d));
        for (IndexType i = 0; i < n; ++i) {
            file.read(reinterpret_cast<char*>(data[i].data()), d * sizeof(DataType));
        }
        file.close();
    } else if (format == "text") {
        // Реализация для текста
        std::ifstream file(filename);
        if (!file.is_open()) return false;
        // ...
    } else {
        if (debug) std::cerr << "Неизвестный формат данных: " << format << std::endl;
        return false;
    }
    if (debug) std::cout << "Данные загружены: " << n << " точек, " << data[0].size() << " размерность" << std::endl;
    return true;
}

void KNNGraphWrapper::build_graph() {
    if (debug) std::cout << "Строим KNN-граф (k=" << k << ")..." << std::endl;

    // Копируем данные в GPU
    cudaMemcpy(d_data, data.data(), n * data[0].size() * sizeof(DataType), cudaMemcpyHostToDevice);

    // Вызываем CUDA ядро для вычисления KNN
    compute_knn_cuda();

    // Считываем результаты обратно
    cudaMemcpy(graph.indices.data(), d_indices, n * k * sizeof(IndexType), cudaMemcpyDeviceToHost);
    cudaMemcpy(graph.distances.data(), d_distances, n * k * sizeof(DistanceType), cudaMemcpyDeviceToHost);

    if (debug) {
        std::cout << "Граф построен. Размер: " << n << " x " << k << std::endl;
        print_graph_stats();
    }
}

void KNNGraphWrapper::refine_graph() {
    if (debug) std::cout << "Уточнение графа..." << std::endl;
    // Пример: локальная оптимизация (например, пересмотр соседей в окрестности)
    // Можно использовать GPU-ядро или CPU
    for (IndexType i = 0; i < n; ++i) {
        // Пример: улучшаем соседей для точки i
        std::vector<std::pair<DistanceType, IndexType>> candidates;
        for (IndexType j = 0; j < n; ++j) {
            if (j == i) continue;
            DistanceType dist = compute_distance(data[i], data[j]);
            candidates.push_back({dist, j});
        }
        std::sort(candidates.begin(), candidates.end());
        for (IndexType idx = 0; idx < k; ++idx) {
            graph.indices[i * k + idx] = candidates[idx].second;
            graph.distances[i * k + idx] = candidates[idx].first;
        }
    }
    if (debug) std::cout << "Граф уточнён." << std::endl;
}

void KNNGraphWrapper::generate_ground_truth() {
    if (debug) std::cout << "Генерация эталонных данных..." << std::endl;
    ground_truth.resize(n);
    for (IndexType i = 0; i < n; ++i) {
        std::vector<std::pair<DistanceType, IndexType>> candidates;
        for (IndexType j = 0; j < n; ++j) {
            if (i != j) {
                DistanceType dist = compute_distance(data[i], data[j]);
                candidates.push_back({dist, j});
            }
        }
        std::sort(candidates.begin(), candidates.end());
        for (IndexType idx = 0; idx < k; ++idx) {
            ground_truth[i].push_back(candidates[idx].second);
        }
    }
    if (debug) std::cout << "Эталонные данные сгенерированы." << std::endl;
}

void KNNGraphWrapper::evaluate_graph() {
    if (ground_truth.empty()) {
        if (debug) std::cerr << "Эталонные данные не сгенерированы!" << std::endl;
        return;
    }

    int correct = 0;
    int total = n * k;

    for (IndexType i = 0; i < n; ++i) {
        for (IndexType j = 0; j < k; ++j) {
            IndexType pred = graph.indices[i * k + j];
            if (std::find(ground_truth[i].begin(), ground_truth[i].end(), pred) != ground_truth[i].end()) {
                correct++;
            }
        }
    }

    float precision = static_cast<float>(correct) / total;
    if (debug) std::cout << "Точность: " << precision << std::endl;
}

std::vector<IndexType> KNNGraphWrapper::query(IndexType query_idx, IndexType k_query) {
    if (k_query == -1) k_query = k;
    std::vector<IndexType> result;
    for (IndexType i = 0; i < k_query; ++i) {
        result.push_back(graph.indices[query_idx * k + i]);
    }
    return result;
}

std::vector<std::vector<IndexType>> KNNGraphWrapper::batch_query(const std::vector<IndexType>& query_indices, IndexType k_query) {
    std::vector<std::vector<IndexType>> results;
    for (IndexType idx : query_indices) {
        results.push_back(query(idx, k_query));
    }
    return results;
}

bool KNNGraphWrapper::save_graph(const std::string& filename) {
    std::ofstream file(filename, std::ios::binary);
    if (!file.is_open()) {
        if (debug) std::cerr << "Ошибка: не удалось открыть файл для записи " << filename << std::endl;
        return false;
    }

    // Записываем n, k
    file.write(reinterpret_cast<const char*>(&n), sizeof(IndexType));
    file.write(reinterpret_cast<const char*>(&k), sizeof(IndexType));

    // Записываем индексы и расстояния
    file.write(reinterpret_cast<const char*>(graph.indices.data()), n * k * sizeof(IndexType));
    file.write(reinterpret_cast<const char*>(graph.distances.data()), n * k * sizeof(DistanceType));

    file.close();
    if (debug) std::cout << "Граф сохранён в " << filename << std::endl;
    return true;
}

bool KNNGraphWrapper::load_graph(const std::string& filename) {
    std::ifstream file(filename, std::ios::binary);
    if (!file.is_open()) {
        if (debug) std::cerr << "Ошибка: не удалось открыть файл " << filename << std::endl;
        return false;
    }

    IndexType loaded_n, loaded_k;
    file.read(reinterpret_cast<char*>(&loaded_n), sizeof(IndexType));
    file.read(reinterpret_cast<char*>(&loaded_k), sizeof(IndexType));

    if (loaded_n != n || loaded_k != k) {
        if (debug) std::cerr << "Несоответствие размеров: ожидаем " << n << "x" << k << ", получено " << loaded_n << "x" << loaded_k << std::endl;
        return false;
    }

    graph.indices.resize(n * k);
    graph.distances.resize(n * k);
    file.read(reinterpret_cast<char*>(graph.indices.data()), n * k * sizeof(IndexType));
    file.read(reinterpret_cast<char*>(graph.distances.data()), n * k * sizeof(DistanceType));

    file.close();
    if (debug) std::cout << "Граф загружен из " << filename << std::endl;
    return true;
}

void KNNGraphWrapper::reset_graph_subset(const std::vector<IndexType>& subset_indices) {
    if (debug) std::cout << "Сброс графа для подмножества размером " << subset_indices.size() << std::endl;

    // Создаём новый граф только для подмножества
    IndexType subset_size = subset_indices.size();
    KNNGraph new_graph;
    new_graph.k = k;
    new_graph.n = subset_size;
    new_graph.indices.resize(subset_size * k);
    new_graph.distances.resize(subset_size * k);

    // Пересчитываем KNN только для подмножества
    std::vector<std::vector<DataType>> subset_data;
    for (IndexType i : subset_indices) {
        subset_data.push_back(data[i]);
    }

    // Пересчёт KNN для подмножества (можно использовать CPU или GPU)
    for (IndexType i = 0; i < subset_size; ++i) {
        std::vector<std::pair<DistanceType, IndexType>> candidates;
        for (IndexType j = 0; j < subset_size; ++j) {
            if (i != j) {
                DistanceType dist = compute_distance(subset_data[i], subset_data[j]);
                candidates.push_back({dist, j});
            }
        }
        std::sort(candidates.begin(), candidates.end());
        for (IndexType idx = 0; idx < k; ++idx) {
            new_graph.indices[i * k + idx] = candidates[idx].second;
            new_graph.distances[i * k + idx] = candidates[idx].first;
        }
    }

    // Обновляем граф
    graph = new_graph;
    n = subset_size;
    if (debug) print_graph_stats();
}

void KNNGraphWrapper::enable_debug(bool debug) {
    this->debug = debug;
}

void KNNGraphWrapper::print_graph_stats() {
    if (!debug) return;
    std::cout << "Граф KNN: " << n << " точек, k=" << k << std::endl;
    std::cout << "Размер: " << n * k * 2 << " элементов (индексы + расстояния)" << std::endl;
    if (!graph.indices.empty()) {
        std::cout << "Пример: индексы для точки 0: ";
        for (IndexType i = 0; i < k; ++i) {
            std::cout << graph.indices[i] << " ";
        }
        std::cout << std::endl;
    }
}
