```cpp
#include "knn_graph.hpp"
#include <cuda.h>
#include <cuda_runtime.h>

__global__ void knn_kernel(const float* data, int n, int d, int k, int* indices, float* distances) {
    int idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx >= n) return;

    float* point = data + idx * d;
    float min_dist = 1e9;
    int* neighbors = indices + idx * k;
    float* dists = distances + idx * k;

    // Инициализация
    for (int i = 0; i < k; ++i) {
        neighbors[i] = -1;
        dists[i] = 1e9;
    }

    // Поиск k ближайших соседей
    for (int j = 0; j < n; ++j) {
        if (j == idx) continue;
        float dist = 0.0f;
        for (int dim = 0; dim < d; ++dim) {
            float diff = point[dim] - data[j * d + dim];
            dist += diff * diff;
        }
        dist = sqrtf(dist);

        // Вставка в k-список
        int pos = k - 1;
        while (pos >= 0 && dist < dists[pos]) {
            dists[pos + 1] = dists[pos];
            neighbors[pos + 1] = neighbors[pos];
            pos--;
        }
        if (pos < k - 1) {
            dists[pos + 1] = dist;
            neighbors[pos + 1] = j;
        }
    }
}

void KNNGraphWrapper::compute_knn_cuda() {
    int d = data[0].size();
    int threads_per_block = 256;
    int blocks = (n + threads_per_block - 1) / threads_per_block;

    knn_kernel<<<blocks, threads_per_block>>>(d_data, n, d, k, d_indices, d_distances);

    cudaError_t err = cudaGetLastError();
    if (err != cudaSuccess) {
        if (debug) std::cerr << "CUDA ошибка: " << cudaGetErrorString(err) << std::endl;
    }
}
