## ✅ 2. Вспомогательные функции (CPU)

```cpp
#include <cuda_runtime.h>
#include <iostream>
#include <vector>
#include <string>
#include <cstdlib>
#include <ctime>

// Инициализация сетки случайными значениями
void initializeGrid(unsigned char* grid, int N, float liveProbability = 0.5) {
    for (int i = 0; i < N * N; ++i) {
        grid[i] = (rand() / (float)RAND_MAX < liveProbability) ? 1 : 0;
    }
}

// Копирование данных из GPU в CPU
void copyFromDevice(unsigned char* hostGrid, unsigned char* deviceGrid, int N) {
    cudaMemcpy(hostGrid, deviceGrid, N * N * sizeof(unsigned char), cudaMemcpyDeviceToHost);
}

// Проверка результата (для регрессионных тестов)
bool verifyResult(unsigned char* expected, unsigned char* actual, int N) {
    for (int i = 0; i < N * N; ++i) {
        if (expected[i] != actual[i]) {
            return false;
        }
    }
    return true;
}

// Запуск симуляции на одном GPU
void runGameOfLifeOnGPU(int gpuId, int N, int iterations, unsigned char* expectedGrid) {
    // Установка устройства
    cudaSetDevice(gpuId);

    // Выделение памяти на GPU
    unsigned char* d_grid;
    unsigned char* d_nextGrid;
    cudaMalloc(&d_grid, N * N * sizeof(unsigned char));
    cudaMalloc(&d_nextGrid, N * N * sizeof(unsigned char));

    // Инициализация сетки на GPU
    unsigned char* h_grid = new unsigned char[N * N];
    initializeGrid(h_grid, N, 0.5);
    cudaMemcpy(d_grid, h_grid, N * N * sizeof(unsigned char), cudaMemcpyHostToDevice);

    // Запуск ядра
    dim3 blockSize(16, 16);
    dim3 gridSize((N + blockSize.x - 1) / blockSize.x, (N + blockSize.y - 1) / blockSize.y);

    for (int i = 0; i < iterations; ++i) {
        gameOfLifeKernel<<<gridSize, blockSize>>>(d_grid, d_nextGrid, N);
        cudaDeviceSynchronize();

        // Обмен буферами
        unsigned char* temp = d_grid;
        d_grid = d_nextGrid;
        d_nextGrid = temp;
    }

    // Копирование результата обратно
    unsigned char* h_result = new unsigned char[N * N];
    cudaMemcpy(h_result, d_grid, N * N * sizeof(unsigned char), cudaMemcpyDeviceToHost);

    // Проверка результата
    if (!verifyResult(expectedGrid, h_result, N)) {
        std::cerr << "Test failed on GPU " << gpuId << "!" << std::endl;
        // Сохранение изображения
        maps_save_image("failed_gpu_output.png", h_result, N, N);
    } else {
        std::cout << "Test passed on GPU " << gpuId << "!" << std::endl;
    }

    // Освобождение памяти
    cudaFree(d_grid);
    cudaFree(d_nextGrid);
    delete[] h_grid;
    delete[] h_result;
}
```

