import androidx.lifecycle.MutableLiveData
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.launch

class PostRepository {

    private val _posts = MutableLiveData<List<Post>>()
    val posts: LiveData<List<Post>> = _posts

    fun getPosts(): LiveData<List<Post>> {
        return posts
    }

    fun refreshPosts() {
        CoroutineScope(Dispatchers.IO).launch {
            // Здесь может быть сетевой запрос или обращение к базе данных
            val newPosts = fetchPostsFromServer()
            _posts.postValue(newPosts)
        }
    }

    private suspend fun fetchPostsFromServer(): List<Post> {
        // Имитация сетевого запроса
        return listOf(
            Post(1, "Title 1", "Content 1"),
            Post(2, "Title 2", "Content 2")
        )
    }
}
```

