## 🧰 Вспомогательные функции

### 1. Запуск на нескольких GPU (MAPS-Multi)

```cpp
void run_simulation_on_gpus(
    unsigned char* h_grid,
    int width,
    int height,
    int num_gpus,
    int num_steps,
    unsigned char* h_expected = nullptr)
{
    int pitch_bytes = width;
    size_t size = width * height;

    unsigned char* h_next = new unsigned char[size];
    std::fill(h_next, h_next + size, 0);

    for (int step = 0; step < num_steps; ++step) {
        for (int gpu_id = 0; gpu_id < num_gpus; ++gpu_id) {
            cudaSetDevice(gpu_id);

            unsigned char* d_current, *d_next;
            cudaMallocPitch(&d_current, &pitch_bytes, width, height);
            cudaMallocPitch(&d_next, &pitch_bytes, width, height);

            cudaMemcpy2D(d_current, pitch_bytes, h_grid, width, width, height, cudaMemcpyHostToDevice);

            dim3 threads(16, 16);
            dim3 blocks((width + threads.x - 1) / threads.x,
                        (height + threads.y - 1) / threads.y);

            game_of_life_kernel<<<blocks, threads>>>(d_current, d_next, width, height, pitch_bytes);

            cudaMemcpy2D(h_next, width, d_next, pitch_bytes, width, height, cudaMemcpyDeviceToHost);

            cudaMemcpy2D(h_grid, width, h_next, width, width, height, cudaMemcpyHostToDevice);

            cudaFree(d_current);
            cudaFree(d_next);
        }

        // Регрессионный тест
        if (h_expected != nullptr) {
            if (!compare_grids(h_grid, h_expected, width, height)) {
                std::cerr << "Regression test failed at step " << step << std::endl;
                save_grid_to_image(h_grid, width, height, "error_step_" + std::to_string(step) + ".png");
                exit(EXIT_FAILURE);
            }
        }
    }

    delete[] h_next;
}
```

