__global__ void compute_distances(float* data, int num_points, int dim, float* query, float* distances) {
    int i = blockIdx.x * blockDim.x + threadIdx.x;
    if (i >= num_points) return;

    float sum = 0.0f;
    for (int d = 0; d < dim; ++d) {
        float diff = data[i * dim + d] - query[d];
        sum += diff * diff;
    }
    distances[i] = sqrtf(sum);
}
