### ✅ Пример реализации на Go:

```go
package main

import (
    "context"
    "fmt"
    "net"
    "net/http"
    "net/url"
    "time"

    "github.com/elastic/go-elasticsearch/v8"
    "github.com/elastic/go-elasticsearch/v8/esapi"
)

// TransportBuilder строит HTTP-транспорт для Elasticsearch
type TransportBuilder struct {
    proxyURL      *url.URL
    proxyUsername string
    proxyPassword string
    disableProxy  bool
    esUsername    string
    esPassword    string
    headers       map[string]string
    timeout       time.Duration
    insecureTLS   bool
}

// NewTransportBuilder создает новый билдер
func NewTransportBuilder() *TransportBuilder {
    return &TransportBuilder{
        headers: make(map[string]string),
    }
}

// SetProxy настраивает прокси
func (b *TransportBuilder) SetProxy(proxyURL, username, password string) *TransportBuilder {
    parsedURL, err := url.Parse(proxyURL)
    if err != nil {
        panic(err)
    }
    b.proxyURL = parsedURL
    b.proxyUsername = username
    b.proxyPassword = password
    return b
}

// DisableProxy отключает использование прокси
func (b *TransportBuilder) DisableProxy() *TransportBuilder {
    b.disableProxy = true
    return b
}

// SetBasicAuth настраивает аутентификацию для Elasticsearch
func (b *TransportBuilder) SetBasicAuth(username, password string) *TransportBuilder {
    b.esUsername = username
    b.esPassword = password
    return b
}

// AddHeader добавляет HTTP-заголовок
func (b *TransportBuilder) AddHeader(key, value string) *TransportBuilder {
    b.headers[key] = value
    return b
}

// SetTimeout устанавливает тайм-аут
func (b *TransportBuilder) SetTimeout(timeout time.Duration) *TransportBuilder {
    b.timeout = timeout
    return b
}

// SetInsecureTLS включает/выключает проверку SSL-сертификата
func (b *TransportBuilder) SetInsecureTLS(insecure bool) *TransportBuilder {
    b.insecureTLS = insecure
    return b
}

// Build создает HTTP-транспорт для Elasticsearch
func (b *TransportBuilder) Build() (*elasticsearch.Client, error) {
    // Настройка прокси
    var proxyFunc func(*http.Request) (*url.URL, error)
    if !b.disableProxy && b.proxyURL != nil {
        proxyFunc = func(*http.Request) (*url.URL, error) {
            return b.proxyURL, nil
        }
    }

    // Создание HTTP-клиента
    tr := &http.Transport{
        Proxy: proxyFunc,
    }

    // Добавление базовой аутентификации к прокси
    if b.proxyURL != nil && b.proxyUsername != "" && b.proxyPassword != "" {
        tr.ProxyConnectHeader = make(http.Header)
        tr.ProxyConnectHeader.Set("Proxy-Authorization", "Basic "+basicAuth(b.proxyUsername, b.proxyPassword))
    }

    // Настройка HTTP-клиента
    client := &http.Client{
        Transport: tr,
        Timeout:   b.timeout,
    }

    // Параметры Elasticsearch
    esConfig := elasticsearch.Config{
        Addresses: []string{"https://elasticsearch-host:9200"},
        BasicAuth: struct {
            Username string
            Password string
        }{
            Username: b.esUsername,
            Password: b.esPassword,
        },
        Headers: b.headers,
        Transport: client.Transport,
        // Проверка SSL-сертификата
        TLS: &elasticsearch.TLSConfig{
            InsecureSkipVerify: b.insecureTLS,
        },
    }

    // Создание клиента Elasticsearch
    esClient, err := elasticsearch.NewClient(esConfig)
    if err != nil {
        return nil, fmt.Errorf("failed to create Elasticsearch client: %w", err)
    }

    // Проверка соединения
    info, err := esClient.Info()
    if err != nil {
        return nil, fmt.Errorf("failed to connect to Elasticsearch: %w", err)
    }

    fmt.Printf("Connected to Elasticsearch %s\n", info.Name)

    return esClient, nil
}

// basicAuth генерирует строку Basic Auth
func basicAuth(username, password string) string {
    return "Basic " + base64.StdEncoding.EncodeToString([]byte(username+":"+password))
}

func main() {
    // Пример использования
    transport := NewTransportBuilder().
        SetProxy("http://proxy.example.com:8080", "proxyuser", "proxypass").
        SetBasicAuth("esuser", "espass").
        AddHeader("X-Request-Id", "12345").
        SetTimeout(10 * time.Second).
        SetInsecureTLS(true)

    client, err := transport.Build()
    if err != nil {
        panic(err)
    }

    // Использование клиента
    req := esapi.IndicesCreateRequest{
        Index: "test-index",
    }

    res, err := req.Do(context.Background(), client)
    if err != nil {
        panic(err)
    }
    defer res.Body.Close()
}
```

