## 🧰 Вспомогательные функции

### 1. Инициализация и запуск на нескольких GPU

```cpp
void run_simulation_on_gpus(
    unsigned char* h_grid,
    int width,
    int height,
    int num_gpus,
    int num_steps,
    unsigned char* h_expected = nullptr)
{
    int deviceCount;
    cudaGetDeviceCount(&deviceCount);
    num_gpus = std::min(num_gpus, deviceCount);

    int chunk_width = width / num_gpus;
    int chunk_height = height;

    for (int dev = 0; dev < num_gpus; ++dev) {
        cudaSetDevice(dev);

        unsigned char* d_current;
        unsigned char* d_next;
        cudaMalloc(&d_current, width * height);
        cudaMalloc(&d_next, width * height);

        cudaMemcpy(d_current, h_grid, width * height, cudaMemcpyHostToDevice);

        dim3 threadsPerBlock(16, 16);
        dim3 blocksPerGrid((width + 15) / 16, (height + 15) / 16);

        for (int step = 0; step < num_steps; ++step) {
            game_of_life_kernel<<<blocksPerGrid, threadsPerBlock>>>(d_current, d_next, width, height);
            cudaDeviceSynchronize();

            // Swap buffers
            std::swap(d_current, d_next);
        }

        cudaMemcpy(h_grid, d_current, width * height, cudaMemcpyDeviceToHost);

        cudaFree(d_current);
        cudaFree(d_next);
    }

    // Проверка результатов
    if (h_expected != nullptr) {
        if (memcmp(h_grid, h_expected, width * height) != 0) {
            std::cerr << "Regression test failed. Saving image..." << std::endl;
            save_grid_as_image(h_grid, width, height, "error_grid.png");
        } else {
            std::cout << "Regression test passed." << std::endl;
        }
    }
}
```

