// TransportBuilder строит HTTP-транспорт для Elasticsearch
type TransportBuilder struct {
    proxyURL      *url.URL
    proxyUsername string
    proxyPassword string
    disableProxy  bool
    esUsername    string
    esPassword    string
    headers       map[string]string
    timeout       time.Duration
    insecureTLS   bool
}

// NewTransportBuilder создает новый билдер
func NewTransportBuilder() *TransportBuilder {
    return &TransportBuilder{
        headers: make(map[string]string),
    }
}

// SetProxy настраивает прокси
func (b *TransportBuilder) SetProxy(proxyURL, username, password string) *TransportBuilder {
    parsedURL, err := url.Parse(proxyURL)
    if err != nil {
        panic(err)
    }
    b.proxyURL = parsedURL
    b.proxyUsername = username
    b.proxyPassword = password
    return b
}

// DisableProxy отключает использование прокси
func (b *TransportBuilder) DisableProxy() *TransportBuilder {
    b.disableProxy = true
    return b
}

// SetBasicAuth настраивает аутентификацию для Elasticsearch
func (b *TransportBuilder) SetBasicAuth(username, password string) *TransportBuilder {
    b.esUsername = username
    b.esPassword = password
    return b
}

// AddHeader добавляет HTTP-заголовок
func (b *TransportBuilder) AddHeader(key, value string) *TransportBuilder {
    b.headers[key] = value
    return b
}

// SetTimeout устанавливает тайм-аут
func (b *TransportBuilder) SetTimeout(timeout time.Duration) *TransportBuilder {
    b.timeout = timeout
    return b
}

// SetInsecureTLS включает/выключает проверку SSL-сертификата
func (b *TransportBuilder) SetInsecureTLS(insecure bool) *TransportBuilder {
    b.insecureTLS = insecure
    return b
}

// Build создает HTTP-транспорт для Elasticsearch
func (b *TransportBuilder) Build() (*elasticsearch.Client, error) {
    // Настройка прокси
