        tr.ProxyConnectHeader.Set("Proxy-Authorization", "Basic "+basicAuth(b.proxyUsername, b.proxyPassword))
    }

    // Настройка HTTP-клиента
    client := &http.Client{
        Transport: tr,
        Timeout:   b.timeout,
    }

    // Параметры Elasticsearch
    esConfig := elasticsearch.Config{
        Addresses: []string{"https://elasticsearch-host:9200"},
        BasicAuth: struct {
            Username string
            Password string
        }{
            Username: b.esUsername,
            Password: b.esPassword,
